    if ($('.school-links>li').length > 0) {

        function getMaxHeight() {
            $('.school-links>li').height("auto");
            var max = Math.max.apply(null, $('.school-links>li').toArray().map(function (li) { return $(li).height(); }));
            return max;
        }

        var setHeightsTimeoutHandle = null;
        $(window).on('resize', function () {
            if (setHeightsTimeoutHandle != null) clearTimeout(setHeightsTimeoutHandle);
            setHeightsTimeoutHandle = setTimeout(function () {
                var height = getMaxHeight();
                console.log(height);
                $('.school-links>li').height(height);
            }, 20);
        });

        $('.school-links>li img').on('load', function () { $(window).trigger('resize'); });
        $(window).trigger('resize');
    }
});

