    $(document).ready(function () {
        var $prevMonthLink = $('section#calendar table.calendar table.calendar > tbody > tr > td:nth-child(1) a');
        var $nextMonthLink = $('section#calendar table.calendar table.calendar > tbody > tr > td:nth-child(3) a');

        $prevMonthLink.html('go to the previous month');
        $nextMonthLink.html('go to the next month');

        var $monthText = $('section#calendar table.calendar table.calendar > tbody > tr > td:nth-child(2)');
        $monthText.attr('tabindex', 0);
        $monthText.attr('role', 'heading');

        var calendarDays = $('section#calendar > table.calendar > tbody > tr:nth-child(n + 2) > td > a');

        var prevMonth = 'March';
        var currentMonth = 'April';
        var nextMonth = 'May';
        var i = 0;

        while (parseInt($(calendarDays[i]).text(), 10) > 1) {
            $(calendarDays[i]).html('' + prevMonth + ' ' + $(calendarDays[i]).text());

            if ($(calendarDays[i]).parent().hasClass('with-event')) {
                $(calendarDays[i]).html($(calendarDays[i]).html() + ', has event');
            }

            i++;
        }

        $(calendarDays[i]).html('' + currentMonth + ' ' + $(calendarDays[i]).text());

        if ($(calendarDays[i]).parent().hasClass('with-event')) {
            $(calendarDays[i]).html($(calendarDays[i]).html() + ', has event');
        }

        i++;

        while (parseInt($(calendarDays[i]).text(), 10) > 1) {
            $(calendarDays[i]).html('' + currentMonth + ' ' + $(calendarDays[i]).text());

            if ($(calendarDays[i]).parent().hasClass('with-event')) {
                $(calendarDays[i]).html($(calendarDays[i]).html() + ', has event');
            }

            i++;
        }

        while (i < calendarDays.length - 1) {
            $(calendarDays[i]).html('' + nextMonth + ' ' + $(calendarDays[i]).text());

            if ($(calendarDays[i]).parent().hasClass('with-event')) {
                $(calendarDays[i]).html($(calendarDays[i]).html() + ', has event');
            }

            i++;
        }

        $('section#calendar').trigger('ADACalendarFixesLoaded');
    });

