function tabs(selectedtab) {   
    // contents
    var s_tab_content = "tab_content_" + selectedtab;  
    var contents = document.getElementsByTagName("div");
    for(var x=0; x<contents.length; x++) {
        name = contents[x].getAttribute("name");
        if (name == 'tab_content') {
            if (contents[x].id == s_tab_content) {
            contents[x].style.display = "block";                       
            } else {
            contents[x].style.display = "none";
            }
        }
    }  
    // tabs
    var s_tab = "tab_" + selectedtab;      
    var tabs = document.getElementsByTagName("a");
    for(var x=0; x<tabs.length; x++) {
        name = tabs[x].getAttribute("name");
        if (name == 'tab') {
            if (tabs[x].id == s_tab) {
            tabs[x].className = "active";                      
            } else {
            tabs[x].className = "";
            }
        }
    }    
}


function social_tabs(selectedtab) {    

    var s_social_tab_content = "social_tab_content_" + selectedtab;   
    var contents = document.getElementsByTagName("div");
    for(var x=0; x<contents.length; x++) {
        name = contents[x].getAttribute("name");
        if (name == 'social_tab_content') {
            if (contents[x].id == s_social_tab_content) { 
            contents[x].style.display = "block"; 
            } else {
            contents[x].style.display = "none"; 
            }
        }
    }   

    var s_social_tab = "social_tab_" + selectedtab;       
    var social_tabs = document.getElementsByTagName("a");
    for(var x=0; x<social_tabs.length; x++) {
        name = social_tabs[x].getAttribute("name");
        if (name == 'social_tab') {
            if (social_tabs[x].id == s_social_tab) {
            social_tabs[x].className = "active ir";                       
            } else {
            social_tabs[x].className = "ir";
            }
        }
    }     
}

