function get_url(name) {
  name=name.replace(/[\[]/,"\\\[").replace(/[\]]/,"\\\]");
  var regexS="[\\?&]"+name+"=([^&#]*)";
  var regex=new RegExp(regexS);

  var results=regex.exec(location.href);

  if(results==null) return '';
  return results[1];
}

