function resizeSlider_ir732() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("shufflehead-ir732-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("shufflehead-ir732-slider").style.height = Math.max((parseFloat("204")/parseFloat("960") * containerWidth), parseFloat("204")) + "px";
	document.getElementById("shufflehead-ir732-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

