	$(document).ready(function () {

		function fixArticleImageSizes() {
			var div = $('.article-image-container').eq(0);
			var width = $(div).width();
			var height = width * 0.55;
			console.log(width + "x" + height);
			$('.article-image-container').height(height);
		}

		var to = null;
		$(window).resize(function () {
			if (to != null) { clearTimeout(to); to = null; }
			to = setTimeout(fixArticleImageSizes, 50);
		});


		var $newsSectionNumber = 1;
		for (var $i = 1; $i <= 4; $i++) {
			if (($i <= ($newsSectionNumber * 3)) & ($i > (($newsSectionNumber - 1) * 3))) {
				var $active = ($('.article.Newsarticle.rows:nth-child(' + $i + ')'));
				$active.removeClass('inactive');
				$active.addClass('active');
			} else {
				var $inactive = ($('.article.Newsarticle.rows:nth-child(' + $i + ')'));
				$inactive.removeClass('active');
				$inactive.addClass('inactive');

			}
		}


		$('.nextNews').click(function () {
			if ($newsSectionNumber < Math.ceil((4 / 3))) {
	            $newsSectionNumber++;
			swapNewsRight($newsSectionNumber);
		}
	    });
	$('.prevNews').click(function () {
		if ($newsSectionNumber > 1) {
			$newsSectionNumber--;
			swapNewsLeft($newsSectionNumber);
		}
	});


	fixArticleImageSizes(); 
    });

	function swapNewsRight(sectionNumber) {
		for (var $i = 1; $i <= 4; $i++) {
			if (($i <= (sectionNumber * 3)) & ($i > ((sectionNumber - 1) * 3))) {
				var $active = ($('.article.Newsarticle.rows:nth-child(' + $i + ')'));
				$active.removeClass('inactive');
				$active.addClass('active');
			} else {
				var $inactive = ($('.article.Newsarticle.rows:nth-child(' + $i + ')'));
				$inactive.removeClass('active');
				$inactive.addClass('inactive');
			}
		}
	}

	function swapNewsLeft(sectionNumber) {
		for (var $i = 1; $i <= 4; $i++) {
			if (($i <= (sectionNumber * 3)) & ($i > ((sectionNumber - 1) * 3))) {
				var $active = ($('.article.Newsarticle.rows:nth-child(' + $i + ')'));
				$active.removeClass('inactive');
				$active.addClass('active');
			} else {
				var $inactive = ($('.article.Newsarticle.rows:nth-child(' + $i + ')'));
				$inactive.removeClass('active');
				$inactive.addClass('inactive');
			}
		}
	}

