	function swapEventsRight() {
		if ($('.activelast').next().length == 0) {
			return;
		}

		var first = $('.activefirst');
		var last = $('.activelast');
		var activenext = $('.activelast').next();

		$('#upcoming-events ul').children().removeClass("active");
		$('#upcoming-events ul').children().addClass("inactive");
		$(last).removeClass('activelast');
		$(first).removeClass('activefirst');

		$(activenext).addClass('activefirst');

		for (var i = 0; i < 4; i++) {
			$(activenext).addClass('active');
			$(activenext).removeClass('inactive');
			if ($(activenext).next().length == 0) {
				$(activenext).addClass('activelast');
				return;
			}
			else if (i == 3) {
				$(activenext).addClass('activelast');
			}
			activenext = $(activenext).next();
		}

	}
	function swapEventsLeft() {
		if ($('.activefirst').prev().length == 0) {
			return;
		}

		var first = $('.activefirst');
		var last = $('.activelast');
		var activenext = $('.activefirst').prev();

		$('#upcoming-events ul').children().removeClass("active");
		$('#upcoming-events ul').children().addClass("inactive");

		$(last).removeClass('activelast');
		$(first).removeClass('activefirst');

		$(activenext).addClass('activelast');

		for (var i = 0; i < 4; i++) {
			$(activenext).addClass('active');
			$(activenext).removeClass('inactive');
			if ($(activenext).prev().length == 0) {
				$(activenext).addClass('activefirst');
				return;
			}
			else if (i == 3) 
			{
				$(activenext).addClass('activefirst');
			}
			activenext = $(activenext).prev();
		}
	}

