			"menuButtonParent"	: "#sp-content",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "PAGES",
			"siteID"			: "4"
		});

		// GLOBAL ICONS
		$("#gb-global-icons").creativeIcons({
			'iconNum'      : '5', // NUMBER OF ICONS
			'iconColor'    : '#FFF', // ICON COLOR
			'icon1'        : ['Athletics/4.png', 'Athletics', 'http://newmilford.schoolwires.net/Page/1490', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon2'        : ['Student Program/4.png', 'Lunch Menus', '/Page/143', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon3'        : ['Education Portal/6.png', 'Powerschool', 'https://nmpower.newmilfordschools.org/public/home.html', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon4'        : ['Information Technology/8.png', 'Employment', '/Page/144', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon5'        : ['People/4.png', 'Directory', '/Page/117', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'siteID'       : '4',
			'siteAlias'    : 'newmilford',
			'calendarLink' : '/Page/2',
			'contactEmail' : 'Webmaster@newmilfordschools.org',
			'onComplete'   : function(){
				$(".global-icon").each(function() {
					var e = $(this);
					$(e).find(".text").prependTo(e);
				});

				var sitemapIcon = ''+
							'SITE MAP'+
							's'+
						   '';
				$(sitemapIcon).appendTo("#gb-global-icons");
			 }
		});


		pgScreenSize();
		useChannelForSection();
		dynamicContent();		
	});

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			mediaRotator();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			mediaRotator();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			mediaRotator();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			mediaRotator();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			mediaRotator();
			appView();
		}
	}

	function athleticsHeadlines() {
		$("#hp-content-bottom .ui-widget.app.headlines").each(function() {
			$(".ui-articles li:eq(0)",this).addClass("athletics-img one");
			$(".ui-articles li:eq(1)",this).addClass("athletics-img two");
			$(".ui-articles li:eq(2)",this).addClass("athletics-img three");
			$(".ui-articles li:eq(3)",this).addClass("athletics-img four");
			$(".ui-articles li:eq(4)",this).addClass("athletics-img five");
			$(".ui-articles li:eq(5)",this).addClass("athletics-img six");
			$(".ui-articles li:eq(6)",this).addClass("athletics-img seven");
			$(".ui-articles li:eq(7)",this).addClass("athletics-img eight");
			$(".ui-articles li:eq(8)",this).addClass("athletics-img nine");

			$(this).find(".ui-article").hover(function() {
				var e = $(this);
				$(this).find("h1.ui-article-title").css({"display":"table", "z-index":"100", "padding":"0 20px", "line-height":"1.2", "max-width":"100%", "box-sizing":"border-box"});
				$(this).find("h1.ui-article-title a").css({"display":"table-cell", "text-align":"center", "vertical-align":"middle"});
				var getNewLink = $(this).find(".ui-article-description").text();

				if(getNewLink == "#" || getNewLink == "" || getNewLink == " ") {
					$(e).find("h1.ui-article-title a").attr("href","javascript:;");
					$(e).find("h1.ui-article-title a").css("cursor","default");
				} else {
					$(e).find("h1.ui-article-title a").attr("href",getNewLink);
				}

			}, function() {
				$(this).find("h1.ui-article-title").hide();
			});
		});
	}

