    function LoadMinibaseList7346(PageIndex){
        var FilterFields = "";
        if ($('#sw-content-minibaselist7346-filterquery').length > 0){
        	FilterFields =encodeURIComponent($('#sw-content-minibaselist7346-filterquery').val());
        }else{
            FilterFields = escape(CreateFilterQuery7346());
        }
        var DisplayType = $("#sw-content-minibaselist7346-displaytype").val();
        if (PageIndex === undefined) {
            PageIndex = $("#sw-content-minibaselist7346-pageindex").val();
        }
        var success = '$("#sw-content-minibaselist7346-pageindex").val(' + PageIndex + ');';
        
        if (0 == 1)
        { window.open( "https://www.northernhighlands.org//site/Default.aspx?DomainID=0&PageID=0&PageType=22&ModuleInstanceID=67&PageModuleInstanceID=7346&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex);
        }
        else 
        { GetContent("https://www.northernhighlands.org//site/UserControls/Minibase/MinibaseListWrapper.aspx?ModuleInstanceID=67&PageModuleInstanceID=7346&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex, "sw-content-minibaselist7346", 2, success);
        }
    
    }
    function LoadMinibaseFilter7346(){
        GetContent("https://www.northernhighlands.org//site/UserControls/Minibase/MinibaseFilterWrapper.aspx?ModuleInstanceID=67&PageModuleInstanceID=7346", "sw-content-minibaselist7346", 2);
    }
    function CreateFilterQuery7346(){
        var filterquery = "";
        $.each($('#sw-content-minibasefilter7346 label'), function(i){
            var id = $(this).attr("for");   
            if($('#' + id).val() != ''){
                var fieldindex = id.substring(id.lastIndexOf('-') + 1);
                //filterquery += $(this).text() 
                
                var comparetype = "E";
                if ($('#sw-content-minibasefilter-field7346' + fieldindex + '-comparetype').val() !== undefined) {
                    comparetype = $('#sw-content-minibasefilter-field7346' + fieldindex + '-comparetype').val();
                }
                filterquery += fieldindex + ":" + comparetype + ":";
                if (comparetype != "E") { 
                    filterquery += $('#' + id).val().replace(/%/g, '[%]').replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                } else {
                    filterquery += $('#' + id).val().replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                }
            }            
            
            /*if(i < ($('#sw-content-minibasefilter67 label').length - 1)) {
                filterquery += ";";
            }*/
        });
        filterquery += "";
        return filterquery;
    }

