function checkAudioTagSupport() {
var bResult = false;
var myAudio = document.createElement('audio');
if (myAudio.canPlayType) {
var canPlayMp3 = !!myAudio.canPlayType && '' != myAudio.canPlayType('audio/mpeg');
if (canPlayMp3) {
bResult = true;
}
}
return bResult
}

input.script:
$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=oxhSErRTPy%2bkrCav43lImg%3d%3d&SessionID=51f04bb5-29d5-4a70-bb4e-40d25b1f251e&SiteID=1&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+9%3a25%3a36+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.87.69';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
});

    $(document).ready(function() {
        $('#dialog-overlay-WindowMedium-base').appendTo('body');
        
        $('body').on('keydown', '.ui-dialog-overlay-base-modal, .ui-dialog-overlay, .ui-sw-alert', function (e) {
            var swAlertOpen = $(".ui-sw-alert").length;
            if (swAlertOpen > 1) {
                if (e.keyCode == 27) {//escape key
                    e.stopImmediatePropagation();
                    e.preventDefault();
                    //get id of open alert
                    var alertboxid = $('.ui-sw-alert').attr('id');
                    //click ok or no
                    if ($('#' + alertboxid + 'ok').length > 0) {
                        $('#' + alertboxid + 'ok').click();
                    } else {
                        $('#' + alertboxid + 'no').click();
                    }
                }
            } else {
                if (e.keyCode == 27) {//escape key
                    e.stopImmediatePropagation();
                    e.preventDefault();
                    // Check if ESC was pressed on DatePicker
                    var raisedByDatepicker = e.target.classList.contains("datepicker");
                    if (!raisedByDatepicker) {
                        e.stopImmediatePropagation();
                        e.preventDefault();
                        // Close Dialog
                        $('.ui-dialog-overlay-close.modal:visible').last().click();
                    } else {
                        // Remove focus
                        e.target.blur();
                        e.target.classList.remove("focus");
                    }
                }
            }
        });
 		
        

    });

