	if (trimString(getId("topLoginUsername"+rn).value) == "" || trimString(getId("topLoginPassword"+rn).value) == "") {
		alert("Please enter your email and password!");
	} else {
		document.getElementsByName("topLoginForm"+rn)[0].submit(); 
	}
}

