		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		//$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");

		//TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
        	$("#sw-mystart-search").unbind();



		//APPEND MYSTART ITEMS TO APPROPRIATE PLACES

		var mystartBtns = "
" + $(".sw-mystart-button.signin").html() + "";
		if($(".sw-mystart-button.register").length){
			mystartBtns += "
" + $(".sw-mystart-button.register").html() + "";
		}
		$("#gb-mystart").append('

User Options' + mystartBtns + '');



		//APPEND SEARCH TO GB-SEARCH-BOTTOM
		$("#sw-mystart-search").addClass("hide768").appendTo("#gb-mystart");

		// ********** HEADER ********** //
		//DOC add sitename
		var siteNameOne = jQuery.trim('Red Bank Regional');
		var siteNameTwo = jQuery.trim('High School District');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Red Bank Regional High School District";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename, .gb.footer.one").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename, .gb.footer.one").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename, .gb.footer.one").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename, .gb.footer.one").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("View All");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add read full story button to each headline
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(".ui-article-description", this).append('Read More');
        		});
        	});
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//MOD EVENTS
		$(".ui-widget.app.upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".ui-widget.app.upcomingevents .ui-articles");

		//CHANGE TEXT OF CALENDAR LINK
		$("a.view-calendar-link").text("View All");

		//SCROLL FUNCTION FOR BACK TO TOP BUTTON
		$(".gb.footer.three .top").click(function(e){
        		e.preventDefault();
        		$("html, body").animate({
				scrollTop: $("body").offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
        	});

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);			
	}

    function stickyCalendarFix() {
        var channelHeight = $("#gb-channel-list-outer").height();

        if ($(window).scrollTop() <= channelHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", channelHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search Our Site");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search Our Site") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search Our Site");
			}
		});
	}

	//REMOVE LOGO ERROR IF NONE SELECTED
	function removeLogo(){
		$("#gb-logo").remove();
		$("#gb-sitename").css({
				"width": "auto",
				"padding-left": "0"
		});
		$("#gb-tagline").css("padding-left","0")
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

		//ADD CLASSES TO EACH UPCOMING EVENT LI IF LIMITING THEM - REMOVE IF NOT NECESSARY
		if($(".hp").length) {

			// ADD CLASSES TO ARTICLES
			$("#hp-region-three .upcomingevents .ui-articles").wrap("
");
			$("#hp-region-three .upcomingevents li").first().addClass("first");
			$("#hp-region-three .upcomingevents li").last().addClass("last");
			var eventClass = ["one", "two", "three", "four", "five"];
			$("#hp-region-three .upcomingevents li").each(function() {
				var thisIndex = $(this).index();
				$(this).addClass("event-" + eventClass[thisIndex]);
			});
			$('#hp-region-three .ui-widget.app.upcomingevents .ui-widget-detail .ui-articles').each(function(){
				$(this).addClass("ui-clear");
			});
		}
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function globalIcons(){
		        var icon1Target = '_self';
		        var icon2Target = '_self';
		        var icon3Target = '_self';
		        var icon4Target = '_self';
		        if(icon1Target == "New Window"){
		        	icon1Target = "_blank";
		        } else {
		        	icon1Target = "_self";
		        }
		        if(icon2Target == "New Window"){
		        	icon2Target = "_blank";
		        } else {
		        	icon2Target = "_self";
		        }
		        if(icon3Target == "New Window"){
		        	icon3Target = "_blank";
		        } else {
		        	icon3Target = "_self";
		        }
		        if(icon4Target == "New Window"){
		        	icon4Target = "_blank";
		        } else {
		        	icon4Target = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", icon1Target);
		        $(".icn.facebook").removeClass("hidden").attr("target", icon2Target);
		        $(".icn.facebook").removeClass("hidden").attr("target", icon3Target);
		        $(".icn.facebook").removeClass("hidden").attr("target", icon4Target);
		}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var twitterCheck = 'true';
		var facebookCheck = 'false';
		var linkedInCheck = 'false';
		var youtubeCheck = 'false';
		var instagramCheck = 'false';
		var googlePlusCheck = 'false';
		var pinterestCheck = 'false';
		var vimeoCheck = 'false';
		var tumblrCheck = 'false';
		var flickrCheck = 'false';

