	var parallaxScroll = function(){
		var getWidth = $(window).width();
		var gbHeaderHeight = ($("#gb-header-outer").height() + 7);
		var channelHeight = $("#gb-channel-list-outer").height();
		if (getWidth >= 768) { // 960+
			if($(window).scrollTop() >= gbHeaderHeight) {
				$("#gb-channel-list-outer").addClass("fixed");
				$("#hp-content-top-outer").css("margin-top", channelHeight);
				$("#sp-content").css("margin-top", channelHeight + 10);
				$("#spn-content").css("margin-top", channelHeight + 10);
			} else {
				$("#gb-channel-list-outer").removeClass("fixed");
				$("#hp-content-top-outer").css("margin-top", "auto");
				$("#sp-content").css("margin-top", "auto");
				$("#spn-content").css("margin-top", "auto");
			}
		}
	}

