        }

        csGlobalJs.OpenInNewWindowWarning();
	});

    function headlinesScroll() {
    	$("#headlines-scroller .region .app.headlines").appScroller({
                "appParent"             : "#headlines-scroller",
                "targetApp"             : "headlines", //upcomingevents or headlines
                "imageNum960"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 960
                "imageNum768"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 768
                "imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
                "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
                "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                "buttonColor"           : "#C54940", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                "extraBackIconClass"    : "",
                "extraNextIconClass"    : "",
                "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
        });

        $("
spotlight").prependTo("#headlines-scroller .ui-widget.app.headlines .ui-widget-header");

        $("#headlines-scroller .ui-widget.app.headlines .ui-widget-detail .ui-articles .ui-article").each(function() {
        	var thisArticle = this;
            var thisApp = $(this).parent().parent().parent().parent();
            if(!$(".cs-article-inner",thisArticle).length) {
                $(thisArticle).wrapInner("
");
                $(".ui-article-thumb",thisArticle).prependTo(thisArticle);
            }
            $(this).addClass("ui-clear");
        });

        $("#headlines-scroller .ui-widget.app.headlines").each(function() {
        	if($("a.more-link",this).length) {
            	$(this).find("a.more-link").appendTo($(this).find(".ui-widget-header"));
            }

            if($(".app-level-social-rss",this).length) {
            	$(this).find(".app-level-social-rss").appendTo($(this).find(".ui-widget-header"));
            }

            $("

").appendTo(this);
            $(".headlines-scroll-ctrl").appendTo(".scroll-btn-container");
        });

        $("#headlines-scroller .headlines-scroll-ctrl.back").text("PREV.");
        $("#headlines-scroller .headlines-scroll-ctrl.next").text("NEXT");

        $("#headlines-scroller .headlines-scroll-ctrl.next").hover(function() {
        	$("#headlines-scroller .scroll-btn-bg").css({
            	"left": "21px"
            });
        }, function() {
        	$("#headlines-scroller .scroll-btn-bg").css({
            	"left": "0"
            });
        });
        $("#headlines-scroller .headlines-scroll-ctrl.back").hover(function() {
        	$("#headlines-scroller .scroll-btn-bg").css({
            	"left": "-21px"
            });
        }, function() {
        	$("#headlines-scroller .scroll-btn-bg").css({
            	"left": "0"
            });
        });

        $(".hp-content-container.one .hp-content-inner #sw-content-container1 > div:eq(0)").show();

        $(".hp-content-container.one").fadeIn();

        var scrollerFocus;

        setInterval(function() {

          $("#headlines-scroller .ui-article-title a, #headlines-scroller .custom-teaser-read-more, #headlines-scroller .headlines-scroll-ctrl").focus(function() {
              scrollerFocus = true;
          });

          $("#headlines-scroller .ui-article-title a, #headlines-scroller .custom-teaser-read-more, #headlines-scroller .headlines-scroll-ctrl").focusout(function() {
              scrollerFocus = false;
          });

          if(scrollerFocus != true) { 
                  $("#headlines-scroller .headlines-scroll-ctrl.next").click();
          }
        }, 4000);



    }

    function csContentScroller() {
    	$("#cs-content-scroller .region .app.upcomingevents").appScroller({
                "appParent"             : "#cs-content-scroller",
                "targetApp"             : "upcomingevents", //upcomingevents or headlines
                "imageNum960"           : 5, //NUMBER OF IMAGES TO DISPLAY AT 960
                "imageNum768"           : 4, //NUMBER OF IMAGES TO DISPLAY AT 768
                "imageNum640"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 640
                "imageNum480"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 480
                "imageNum320"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 320
                "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                "buttonColor"           : "#C54940", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                "extraBackIconClass"    : "rumson-icon-font",
                "extraNextIconClass"    : "rumson-icon-font",
                "controlGutter"         : "40" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
        });

        $("#cs-content-scroller .ui-widget.app.upcomingevents .ui-articles").addClass("displayFlexRow");

        $("#cs-content-scroller .ui-widget.app.upcomingevents .ui-articles .ui-article").each(function() {
        	var thisArticle = this;
            var thisArticleLink = $(this).find(".sw-calendar-block-title a").attr("href");
            if(!$(".event-extra-link",this).length) {
            	$("+").appendTo($(thisArticle).parent());
            }
        });

        $("NEXT EVENT").prependTo("#cs-content-scroller div.ui-widget.app.upcomingevents .ui-articles li:first-child");
        $(".view-calendar-link span").text("View Full Calendar");

        $("#cs-content-scroller .ui-widget.app.upcomingevents").each(function() {
        	$($(this).find(".ui-widget-footer")).before($(this).find(".headlines-scroll-ctrl.next"));
            $($(this).find(".ui-widget-header")).prependTo($(this).find(".ui-widget-footer"));
            if($(".ui-widget-footer .ui-widget-header",this).length) {
        		$(this).find(".ui-widget-detail .view-calendar-link").appendTo($(this).find(".ui-widget-footer .ui-widget-header"));
            } else {
            	$(this).find(".ui-widget-detail .view-calendar-link").appendTo($(this).find(".ui-widget-footer"));
            }
            $(this).find(".ui-widget-footer").addClass("ui-clear");
        });
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				if($("#hp-slideshow .rs-gallery-images .scroll-down-btn").length) {
                    $("#gb-mystart-outer").before($(".scroll-down-btn"));
                }
                if($("#hp-slideshow .rs-gallery-images .gallery-controls-outer").length) {
                	$(".gallery-controls-outer").appendTo("#hp-slideshow .rs-gallery-container");
                }
                if($("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail .scroll-btn-container").length) {
                	$(".scroll-btn-container").appendTo("#headlines-scroller div.ui-widget.app.headlines");
                }
			break;
			case "768":
				if($("#hp-slideshow .rs-gallery-images .scroll-down-btn").length) {
                    $("#gb-mystart-outer").before($(".scroll-down-btn"));
                }
                if($("#hp-slideshow .rs-gallery-images .gallery-controls-outer").length) {
                	$(".gallery-controls-outer").appendTo("#hp-slideshow .rs-gallery-container");
                }
                if($("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail .scroll-btn-container").length) {
                	$(".scroll-btn-container").appendTo("#headlines-scroller div.ui-widget.app.headlines");
                }
			break;
			case "640":
                if(!$("#hp-slideshow .rs-gallery-images .scroll-down-btn").length) {
                	$(".scroll-down-btn").appendTo("#hp-slideshow .rs-gallery-images-list");
                }
                if(!$("#hp-slideshow .rs-gallery-images .gallery-controls-outer").length) {
                	$(".gallery-controls-outer").appendTo("#hp-slideshow .rs-gallery-images-list");
                }
                if($("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail .scroll-btn-container").length) {
                	$(".scroll-btn-container").appendTo("#headlines-scroller div.ui-widget.app.headlines");
                }
			break;
			case "480":
            	appView();
                $("#headlines-scroller div.ui-widget.app.headlines").addClass("active");
            	if(!$("#hp-slideshow .rs-gallery-images .scroll-down-btn").length) {
                	$(".scroll-down-btn").appendTo("#hp-slideshow .rs-gallery-images-list");
                }
                if(!$("#hp-slideshow .rs-gallery-images .gallery-controls-outer").length) {
                	$(".gallery-controls-outer").appendTo("#hp-slideshow .rs-gallery-images-list");
                }
                if(!$("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail .scroll-btn-container").length) {
                	$(".scroll-btn-container").appendTo("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail");
                }
                csContentScroller();
			break;
			case "320":
            	appView();
                $("#headlines-scroller div.ui-widget.app.headlines").addClass("active");
            	if(!$("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail .scroll-btn-container").length) {
                	$(".scroll-btn-container").appendTo("#headlines-scroller div.ui-widget.app.headlines .ui-widget-detail");
                }
                csContentScroller();
			break;
		}
	}

