                    	$(".rs-gallery-desc").removeClass("no-caption");
                    },
		        	"onTransitionEnd" 			: function() {
                    	if($(".rs-caption").hasClass("hidden")) {
                        	$(".rs-gallery-desc").addClass("no-caption");
