			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function dynamicContent(){
		// ********** MYSTART ********** //
        $("#sw-mystart-account #sw-myaccount").html('user options ');
        //DOC if myview button exists, move into myaccount dropdown

        if($(".sw-mystart-button.signin").length) {
        	$(".sw-mystart-button.signin").prependTo(".cs-mystart.right");
        }
        if($(".sw-mystart-button.register").length) {
        	$(".sw-mystart-button.register").appendTo(".cs-mystart.right");
        }

        if($(".sw-mystart-button.signin").length) {
        	$(".sw-mystart-button.signin > a span").text("staff sign in");
        }

        // ADJUST PASSKEY OVERLAY
        $(document).ajaxComplete(function() {
            if($("#ui-mypasskey-overlay").length && !$("#sw-mystart-mypasskey #ui-mypasskey-overlay").length) {
                $("#sw-mystart-mypasskey").append($("#ui-mypasskey-overlay"));
            }
        });

        //SEARCH
        $("#sw-search-input").attr("tabindex","-1").prependTo("#gb-search-input-cont");
        $(".gb-search-icon").click(function() {
        	if($("#gb-search-input-cont").hasClass("open")) {
            	$(".gb-search").removeClass("open");
            	$("#gb-search-input-cont").removeClass("open").attr({
                    	"aria-expanded": "false",
                        "aria-hidden": "true"
                });
                $(".gb-search-icon").attr("aria-label","Open Search Input");
                $(".close-search-btn").attr("tabindex","-1");
                $("#sw-search-input").attr("tabindex","-1");
            } else {
            	$(".gb-search").addClass("open");
            	$("#gb-search-input-cont").addClass("open").attr({
                    	"aria-expanded": "true",
                        "aria-hidden": "false"
                });
                $(".gb-search-icon").attr("aria-label","Close Search Input");
                $(".close-search-btn").attr("tabindex","0");
                $("#sw-search-input").attr("tabindex","0");
            }
        });
        $(".gb-search-icon").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                if($("#gb-search-input-cont").hasClass("open")) {
                	$(".gb-search").removeClass("open");
                    $("#gb-search-input-cont").removeClass("open").attr({
                    	"aria-expanded": "false",
                        "aria-hidden": "true"
                    });
                    $(".close-search-btn").attr("tabindex","-1");
                    $("#sw-search-input").attr("tabindex","-1");
                } else {
                	$(".gb-search").addClass("open");
                    $("#gb-search-input-cont").addClass("open").attr({
                    	"aria-expanded": "true",
                        "aria-hidden": "false"
                    });
                    $(".close-search-btn").attr("tabindex","0");
                    $("#sw-search-input").attr("tabindex","0");
                    setTimeout(function() {
                        $("#sw-search-input").focus();
                    }, 300);
                }
            }
        });
        $("#sw-search-input").focusout(function() {
        	setTimeout(function() {
        		$(".gb-search").removeClass("open");
            	$("#gb-search-input-cont").removeClass("open").attr({
                    	"aria-expanded": "false",
                        "aria-hidden": "true"
                });
                $(".gb-search-icon").attr("aria-label","Open Search Input");
                $(".close-search-btn").attr("tabindex","-1");
                $("#sw-search-input").attr("tabindex","-1");
            }, 300);
        });

        $(".close-search-btn").click(function() {
        	$("#gb-search-input-cont").removeClass("open").attr({
                "aria-expanded": "false",
                "aria-hidden": "true"
            });
            $(".gb-search-icon").attr("aria-label","Open Search Icon");
            $(".close-search-btn").attr("tabindex","-1");
            $("#sw-search-input").attr("tabindex","-1");
        });
        $(".close-search-btn").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                $("#gb-search-input-cont").removeClass("open").attr({
                    	"aria-expanded": "false",
                        "aria-hidden": "true"
                });
                $(".gb-search-icon").attr("aria-label","Open Search Icon").focus();
                $(".close-search-btn").attr("tabindex","-1");
                $("#sw-search-input").attr("tabindex","-1");
            }
        });
        $(".close-search-btn").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();
                    $("#sw-search-input").focus();
                break;
            }
        });
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();
        $("#sw-search-input").before('Search');
        $("#sw-search-button").attr({
        	"role": "button",
            "aria-label": "Search Our Site"
        });
        $("#sw-search-button").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                SWGoToSearchResultsPageswsearchinput();
            }
        });

		//DOC change schoollist selector text
