link.script:
$(document).ready(function () {
    $(document).konami({
        result: Falling
    });
    $("nav ul li ul").dropdownMaxHeight();
});
function Falling() {
    var fallingElement;
    fallingElement = $("body *")[Math.floor(Math.random() * $("body *").length)];
    //fallingElement = $("header")[0];
    var start = 0;
    var dnone = false;
    var pnone = false;
    var ppnone = false;
    if ($(fallingElement).css("position") == "static") {
        $(fallingElement).css("position", "relative")
    }
    else {
        start = $(fallingElement).css("top").replace("px", "");
    }
    if (start == "auto") {
        start = 0;
    }
    var t = 0;
    var grav = 0.2;
    //console.log($("body *").length);
    //console.log(fallingElement);
    var interval = setInterval(function () {
        t += grav;
        start += t;
        //console.log(start);
        $(fallingElement).css("top", start.toString() + "px");
        var xh = $("body > form").outerHeight();
        if (window.innerHeight > $("body > form").outerHeight()) {
            xh = window.innerHeight;
        }
        if (xh < start || $(fallingElement).css("display") == "none" || $(fallingElement).parent().css("display") == "none" || $(fallingElement).parent().parent().css("display") == "none") {
            $(fallingElement).remove();
            //console.log($("body *").length + "x");
            if (Math.random() > 0.2)
                clearInterval(interval);
            start = 0;
            t = 0;
            //console.log($("body *").length + "xx");
            if ($("body *").length > 0) {
                Falling();
            }
        }
    }, 10);
}

