$(document).ready(function(){
   $(".ui-article>.ui-maps-citystatezip").each(function(){
	if($(this).children("#hidCity").val()=="" || $(this).children("#hidState").val()==""){
		$(this).children("#commaCity").hide();	
	}   
	if($(this).children("#hidState").val()=="" || $(this).children("#hidZipCode").val()==""){
		$(this).children("#commaState").hide();	
	}	
   });

});

