        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429093033640';
        var calMonthFullId = 'miniCalMonthFull20200429093033640';
        var calMonthShortId = 'miniCalMonthShort20200429093033640';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429093033640';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429093033640';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

