function resize_sp1181() {
	var windowWidth = Math.max(parseFloat("600"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel1181Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel1181Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel1181Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel1181Div").style.height = Math.max((parseFloat("360")/parseFloat("600") * windowWidth), parseFloat("360")) + "px";
	document.getElementById("carousel1181Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel1181Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

