$(document).ready(function () {
    $.each($(".hpTitle"), function (index, value) {
        var text = $(value)[0].innerHTML.split(" ");
        if (text.length > 1) {
            $(value)[0].innerHTML = text[0] + " ";
            var newText = "";
            for (var i = 1; i < text.length; i++) {
                newText = text[i] + " ";
            }
            var b = document.createElement("b");
            b.innerHTML = newText;
            $(b).appendTo(value);
        }
    });
});

