var h;
setInterval(function () {
    if (window.innerWidth > 767) {
        h = $(".MV").height() + 100;
        $(".MissionVision").css("height", h + "px");
        $(".Super").css("height", h + "px");
    }
    else {
        $(".MissionVision").css("height", "");
    }
}, 1);
