$("#searchbutton").on('mouseover touchstart', function(){ 
  if(t){
      clearTimeout(t);
      t = undefined;
  }
  z = z + 1;
  document.getElementById("searchbox").style.zIndex = z;
  $("#searchbox").slideDown();
}); 

