 function calQtip() {
    // By suppling no content attribute, the library uses each elements title attribute by default
   $('a.c[title]').qtip({
      content: {
         text: false // Use each elements title attribute
      },
	  position: {
      corner: {
         target: 'leftMiddle',
         tooltip: 'rightMiddle'
      }
   },
      style:{
	        'font-size': 12, 
			'line-height': '16px',
			'margin': '0px',
			'font-family': 'verdana', 
			'color': '#002266',
			'background': '#eef8ff',
	        border: {
               width: 2,
               radius: 4,
               color: '#ff6600'
            },
        width: { min: 0, max: 320},
        //name: 'red', 
	    tip: true  // Give it some style
	  },
	  effect: {
	     type: 'fade',
		 length: 100
	  }
   });
 }
