		if(useTranslate == "off" || useTranslate != "on")
		{
			$(".sw-mystart-dropdown.translate").hide();
		}
	}
function schoolLevel()
	{
		if(schoolTextFiles == "on")
		{
			$.ajax({
			  url: "/cms/lib/NY19000591/Centricity/Template/3/setup/forestville.txt",
			  dataType: "script",
			  success: function() {
			    initialize();
			  },
			  error: function()
			  {
			  	alert("An error had occured in initializing setup files.");
			  }
			});
		}
		else
		{
			initialize();
		}

	}
	function hideLogo(){
		if(customLogos == "on")
		{
			$("#gb-logo a img").attr("src","/cms/lib/NY19000591/Centricity/Template/3/logos/forestville.png");
			$("#sp-logo a img").attr("src","/cms/lib/NY19000591/Centricity/Template/3/logos/forestville-subpage.png");
		}
		else if(customLogos == "off")
		{
			$("#gb-logo a img").attr("src","/cms/lib/NY19000591/Centricity/Template/3/logos/_logo.png");
			$("#sp-logo a img").attr("src","/cms/lib/NY19000591/Centricity/Template/3/logos/_subpage-logo.png");
		}
		else
		{
			$("#gb-logo").hide();
			$("#sp-logo").hide();

		}
	}
	function checkIcons(){
	// This functions checks for global icons and displays them properly if they exist
	if(iconSwitch == "on")

		{
			for(i=0; i<numberIcons; i++)
			{ 
				globalLink = eval("icon" + (i+1) + "Link");
				globalTarget = eval("icon" + (i+1) + "Target");
				globalAlt = eval("icon" + (i+1) + "Alt");
				//CHECK FOR SITEALIAS STRING
				globalLink = globalLink.replace("[SITEALIAS]","forestville");
				//CHECK FOR GLOBAL CALENDAR LINK
				globalLink = globalLink.replace("[SITECALENDARLINK]","/Page/2");
				//CHECK FOR GLOBAL SITEMAP LINK
				globalLink = globalLink.replace("[SITEMAPLINK]","/site/Default.aspx?PageType=15&SiteID=1&SectionMax=15&DirectoryType=6]");

				$("#gb-global-icons").append("");
				$("#sp-global-container").append("");
			}
		}
		else
		{
			$("#gb-global-icons").hide();
			$("#sp-global-icons").hide();
		}	

	}


	function sameHeight(group){
		group.height(group.parent().height());
		group.children().resize(function() {
			group.height("");
	      		group.height(group.parent().height());	
                });
	}
	function checkDistrict(){
		if (districtButton == "off"){
			$(".sw-mystart-button.home").hide();
		}
	}
	function loadFlash(){
		if($("#hp-upper-curve").size()){
	            var so = new SWFObject("/cms/lib/NY19000591/Centricity/Template/3/lt-verticalCurve-uppercurve.swf","mymovie", "565", "282", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("primaryColor", primaryColor);
	            so.addVariable("secondaryColor", secondaryColor);
	            so.write("hp-upper-curve");
	        }
        	if($("#sp-upper-curve").size()){
        	    var so = new SWFObject("/cms/lib/NY19000591/Centricity/Template/3/lt-verticalCurve-secondaryPage.swf","mymovie", "100", "120", "9", "#FFFFFF");
	            so.addParam("wmode", "transparent");
	            so.addVariable("secondaryColor", secondaryColor);
	            so.write("sp-upper-curve");

        	}  

	}
	function schoolName(){
		//sets school name
		if (schoolNameText == "default"){
			$(".school-name").html("Forestville Central School");		

		}

		else {

			$(".school-name").html(schoolNameText);	


		}

		var topValue = schoolNameTopSpacing + "px";
		$("#hp-header .school-name").css("padding-top", topValue).css("color", schoolNameColor);
		var leftValue = schoolNameLeftSpacing + "px";
		$(".sp.school-name").css("padding-left", leftValue).css("color", schoolNameColor);
		//sets tagline
		$(".school-slogan").html(taglineText);
		var topValue = tagLineTopSpacing + "px";
		$("#hp-header .school-slogan").css("padding-top", topValue).css("color", taglineColor);
		var leftValue = tagLineLeftSpacing + "px";
		$("#sp-header .school-slogan").css("padding-left", leftValue).css("color", taglineColor);



	}
	function changeAllColors(){
		 //Homepage header colors
		 $("body").css("background-color", backgroundColor);
		 $("#hp-header, #sp-header").css("background-color", primaryColor);
		 $("ul.ui-breadcrumbs a").css("color", primaryColor);
		 //Navigation Colors
		 $("#gb-channel-list").css("background-color", secondaryColor);
	         $("li.sw-channel-item > a").css("color", channelUnselectedColor);
	         $("li.sw-channel-item.active > a span").css("color", channelSelectedColor);
	         $("li.sw-channel-item > a").hover(function()
	         {
	             $(this).css("color", channelSelectedColor);
	         },
	         function()
	         {
	             $(this).css("color",channelUnselectedColor);
	             $("li.sw-channel-item.active > a span").css("color", channelSelectedColor);
	         });
	         //Region One Colors
