	         $(".hp.column.one .ui-widget-detail").css("color", regionOneBodyColor);
	         $(".hp.column.one a, ul.sw-pagenavigation-control li > a").css("color",regionOneLinkColor);
	         //Region Two  Colors
	         $(".hp.column.two").css("background-color", secondaryColor);
	         $("#hp-bottom-curve").css("background-color", secondaryColor);
	         $(".hp.column.two div.ui-widget.app .ui-widget-header h1").css("color", regionTwoHeaderColor);
	         $(".hp.column.two .ui-widget-detail").css("color", regionTwoBodyColor);
	         $(".hp.column.two a").css("color",regionTwoLinkColor);
	         //Region Three Colors
	         $(".hp.column.three").css("background-color", secondaryColor);
	         $(".hp.column.three div.ui-widget.app .ui-widget-header h1").css("color", regionThreeHeaderColor);
	         $(".hp.column.three .ui-widget-detail").css("color", regionThreeBodyColor);
	         $(".hp.column.three a").css("color",regionThreeLinkColor);
	         //Footer colors
	         $("#gb-footer").css("background-color", accentColor);
	         $("#gb-footer, #gb-footer a").css("color", footerTextColor);
