        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/NY01913810/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append("");
                } else {
                    $("#gb-logo").append("");
                }
            }

            // ADD SITENAME
            var siteNameOne = $.trim("Enlarged City School District of Middletown");
            var siteTagOne = $.trim("High expectations are meaningless without rich opportunities");
            var siteTagTwo = $.trim("Personalizing education... not standardizing it");
            if(siteNameOne != "") {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            }
            if(siteTagOne != "") {
            	$("#gb-sitename").append("
" + siteTagOne + "");
            }
            if(siteTagTwo != "") {
            	$("#gb-sitename").append("
" + siteTagTwo + "");
            }
        },

        "Schools": function(){

            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 1422,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    Additoinal Fields: schoolList.schoolAddress, schoolList.schoolCity, schoolList.schoolState, schoolList.schoolZip, schoolList.schoolPhone
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += "

" + schoolList[i].schoolName + "
" + schoolList[i].schoolType + "
" + schoolList[i].schoolAddress + "" + schoolList[i].schoolCity + ", " + schoolList[i].schoolState + " " + schoolList[i].schoolZip + "P: " + schoolList[i].schoolPhone + "
VISIT SITE";
                        }
                    }

                    schools += '';

                    $('#cj-school-modal-wrapper .cj-modal-inner').html(schools);
                }
            });

        },

