                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            if('Yes' == 'Yes;No' || 'Yes' == 'Yes') {
            	$('#gb-page nav #channel-navigation #navc-HP > a').html('Home');
