        "Dropdown": function() {

            //--> Dropdown funciton
            var closeDrop;
            var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            $('.gb-mystart-btn:not(".search"):not(".school")').unbind().click(function(e){

                var dropdown = $(this).attr('aria-controls');

                e.preventDefault();

                if($(this).hasClass('open')) {

                    $('#' + dropdown).removeClass('open').attr('aria-hidden', 'true').stop(true, true).slideUp();
                    $(this).removeClass('open').attr('aria-expanded', 'false');
                    $('.gb-dropdown a').attr('tabindex', '-1');

                } else if($(this).hasClass('inner')) {

                    $('#' + dropdown).addClass('open').attr('aria-hidden', 'false').stop(true, true).slideDown();
                    $(this).addClass('open').attr('aria-expanded', 'true');
                    $('.gb-dropdown a').attr('tabindex', '-1');

                    setTimeout(function(){

                    	$('#' + dropdown + ' a').first().focus();

                    }, 300);

                } else {

                    $('#' + dropdown).addClass('open').attr('aria-hidden', 'false').stop(true, true).slideDown();
                    $(this).addClass('open').attr('aria-expanded', 'true');
                    $('.gb-dropdown a').attr('tabindex', '-1');

                }


            }).hover(function(){

                clearTimeout(closeDrop);

            }, function(){

                var id = $(this).attr('id');

                if($(this).hasClass('open')) {

                    closeDrop = setTimeout(function(){

                        $('#' + id).click();

                    }, 300);

                }

            }).keydown(function(e){

                var c = e.which;

                if(c == KeyCodes.down){

                    e.preventDefault();
                    $(this).addClass('inner').click();

                }

                if(c == KeyCodes.enter || c == KeyCodes.space ) {

                	e.preventDefault();
                    if(!$(this).hasClass('open')) {
                    	$(this).addClass('inner').click();
                    } else {
                    	$(this).click();
                    }

                }

            }).blur(function(){

                var id = $(this).attr('id');

                if($('#' + id).hasClass('open')) {

                    closeDrop = setTimeout(function(){

                        $('#' + id).click();

                    }, 300);

                }

            });

            $('.gb-dropdown').unbind().hover(function(){

                clearTimeout(closeDrop);

            }, function(){

                var id = $(this).attr('id').replace('dropdown', 'btn');
				if($(this).hasClass('open')) {

                    closeDrop = setTimeout(function(){

                        $('#' + id).focus().click();

                    }, 300);

                }

            });

            $('.gb-dropdown a').unbind().keydown(function(e){

                var c = e.which,
                    first = $(this).parent().first().children(),
                    last = $(this).parent().last().children(),
                    next = $(this).parent().next().children(),
                    prev = $(this).parent().prev().children(),
                    btn = $('#' + $(this).closest('.gb-dropdown').attr('id').replace('dropdown', 'btn'));

                if(c == KeyCodes.down || c == KeyCodes.up || c == KeyCodes.left || c == KeyCodes.right) {

                    e.preventDefault();

                    if(c == KeyCodes.down && next.html() == undefined) {

                        first.focus();

                    } else if(c == KeyCodes.up && prev.html() == undefined) {

                        btn.removeClass('inner').focus();

                    } else if(c == KeyCodes.down) {

                        next.focus();

                    } else if(c == KeyCodes.up) {

                        prev.focus();

                    }

                }

            }).focus(function(){

                clearTimeout(closeDrop);

            }).blur(function(){


                var id = $(this).closest('.gb-dropdown').attr('id').replace('dropdown', 'btn');

                closeDrop = setTimeout(function(){

                    $('#' + id).removeClass('inner').click();

                }, 100);

            });


            //--> Modal control
            $('.cj-modal-btn').click(function(){

            	var modal = $(this).attr('aria-controls');
               	var id = $(this).attr('id');

            	if($(this).hasClass('open')) {
                	$('#' + modal).removeClass('open').fadeOut().attr('aria-hidden', 'true');
                    $('#gb-page, #skipLink').removeClass('cj-modal-open');
                    $('#'+modal+' button, #'+modal+' a').attr('tabindex', '-1');
                    if(modal == 'cj-school-modal-wrapper') {
                    	var position = $('#gb-school-btn').offset();
                    	$('.school-tile a').attr('tabindex', '-1');
                        $('#cj-school-cls-btn').attr('aria-hidden', 'true').attr('tabindex', '-1');
                	}
                    $('#' + id).focus();
                } else {
                	$('#' + modal).addClass('open').fadeIn().attr('aria-hidden', 'false');
                    $('#gb-page, #skipLink').addClass('cj-modal-open');
                    $('#'+modal+' button, #'+modal+' a').attr('tabindex', '0');
                    if(modal == 'cj-school-modal-wrapper') {
                    	var position = $('#gb-school-btn').offset();
                        $('#cj-school-cls-btn').attr('aria-hidden', 'false').attr('tabindex', '0')
                        	.css({
                        	"top" : position.top + "px",
                            "left" : (position.left + 136) + "px"
                        });
                        $('#' + modal + ' .cj-modal-inner').css('top', ($('#cj-school-cls-btn').offset().top + 65) + 'px');
                        $('#cj-school-modal-wrapper a').first().focus();
                	} else if(modal == 'cj-search-modal-wrapper') {

                    	var position = $('#gb-search-btn').offset();
                        $('#cj-search-cls-btn').attr('aria-hidden', 'false').attr('tabindex', '0')
                        	.css({
                        	"top" : position.top + "px",
                            "left" : position.left + "px"
                        });

                        $('#' + modal + ' .cj-modal-inner').css('top', ($('#cj-search-cls-btn').offset().top + 85) + 'px');
                        $('#sw-search-input').focus();

                    }
                }

            });

            //-> Keep enter from submitting search
            $('#gb-search-btn').keydown(function(e){
            	var c = e.which;
            	if(c == KeyCodes.enter) {
                	return false;
                }
            }).keyup(function(e){
            	var c = e.which;
                if(c == KeyCodes.enter) {
                	$(this).click();
                }
            });

            $('#cj-school-cls-btn').click(function(){

            	var modal = $(this).attr('aria-controls');

                $('#' + modal).removeClass('open').fadeOut();
                $('#gb-page').removeClass('cj-modal-open');
                $('.school-tile a').attr('tabindex', '-1');
                $('#gb-school-btn').focus();

            });

            $('#cj-search-cls-btn').click(function(){

            	var modal = $(this).attr('aria-controls');

                $('#' + modal).removeClass('open').fadeOut();
                $('#gb-page').removeClass('cj-modal-open');
                $('#cj-search-modal-wrapper a').attr('tabindex', '-1');
                $('#gb-search-btn').focus();

            }).keydown(function(e){
            	var c = e.which;
            	if(c == 32 || c == 13) {
                	return false;
                }
            }).keyup(function(e){
            	var c = e.which;
            	if(c == 32 || c == 13) {
                	$(this).click();
                }
            });

            $('#gb-translate-btn').focus(function(){
            	if($('#gb-page').hasClass('cj-modal-open')) {
                	$('.cj-modal-wrapper.open button.cs-btn-reset').first().focus();
                }
            });

			$('#skipLink').focus(function(){
            	if($(this).hasClass('cj-modal-open')) {
                	$('.cj-modal-wrapper.open button.cs-btn-reset').first().focus();
                }
            });
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
