// Bug 76422

		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker5 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker5 a.ui-datepicker-prev').click();
	    		handlePrevClick5();
	    	}
	    });
	    $('#datepicker5 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker5 a.ui-datepicker-next').click();
	    		handleNextClick5();
	    	}
	    });


		function handlePrevClick5(){
			setTimeout(function () {
				triggerClick5();
			    $('#datepicker5').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick5(){
			setTimeout(function () {
				triggerClick5();
			    $('#datepicker5').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick5(){
			prev = $('#datepicker5').find('.ui-datepicker-prev');
			next = $('#datepicker5').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick5);
			$(prev).on('click', handlePrevClick5);
		}

		};



$(document).ready(function() {
var server = 'no';
if (server=='yes') {
	var firstLoad5 = true;
}else{
	var firstLoad5 = ($.fn.jquery.split('.')[1])<6?true:false;
}
var doe=[];
var load_doe5=function(y,m,callback){
  if(y.length!=4||m.length!=2) return;
  $.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/doe/'+y+'/'+m, callback);
}
var proc_doe5=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe5(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

var cal_init5=function(){
  proc_doe5('2020','04',function(){
    $('#cal_loadimg5').hide();
    jQuery('#datepicker5').datepicker({
      prevText: ' ',
      nextText: ' ',
      onSelect: function(dateText, inst) {
//       	inst.inline = false;
        if (!firstLoad5) {
          var calendar_url = 'https://as2.rschooltoday.com/public/genie/1101/school/2/';
          if(calendar_url.length<3)return;
          var selected_date = new Date(dateText);
          var additional_url = 'date/' +selected_date.getFullYear()+'-'+ (parseInt(selected_date.getMonth()) + 1) +'-' + selected_date.getDate() + '/view/day/';
calendar_url += additional_url;
          window.open(calendar_url,'_blank');
        }
        firstLoad5 = false;
      },
      onChangeMonthYear: function(y,m,obj){
        y+='';m=(m<10?'0':'')+m;
        doe.indexOf(y+'-'+m)==-1&&proc_doe5(y,m,function(){
          console.log(doe);
          jQuery('#datepicker5').datepicker();
        });
        setTimeout(function () {

		$('#datepicker5 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker5 a.ui-datepicker-prev').click();
	    		handlePrevClick5();
	    	}
	    });
	    $('#datepicker5 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker5 a.ui-datepicker-next').click();
	    		handleNextClick5();
	    	}
	    });


		function handlePrevClick5(){
			setTimeout(function () {
				triggerClick5();
			    $('#datepicker5').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick5(){
			setTimeout(function () {
				triggerClick5();
			    $('#datepicker5').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick5(){
			prev = $('#datepicker5').find('.ui-datepicker-prev');
			next = $('#datepicker5').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick5);
			$(prev).on('click', handlePrevClick5);
		}

        }, 50);
      },
      beforeShowDay : function(date) {
        var y=date.getFullYear()+'',m=date.getMonth()+1,d=date.getDate();
        m=(m<10?'0':'')+m;d=(d<10?'0':'')+d;
        return [ true, doe.indexOf(y+'-'+m+'-'+d)>0?'call-bold':'', '' ];
      },
    });
  });
}
