$(".group871252").colorbox({rel:'group871252', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group871252").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery871252 = $("a[rel=group871252]").colorbox({maxWidth: "80%"});
$("a#openGallery871252").click(function(e){
    e.preventDefault();
    $gallery871252.eq(0).click();
});

