                    if(jQuery.trim(megaMenuData[i].channelCaption) != ""){
                    	var captionColumn = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                    	$(".sw-channel-dropdown .mega-columns .mega-menu-left", this).prepend(captionColumn);
                    }

                    // ADD CHANNEL NAME
                    var channelColumn = '
' + channelTextReg + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-left", this).prepend(channelColumn);

                    // ADD READ MORE
                    if(jQuery.trim(megaMenuData[i].learnMoreLink) != ""){
                    	var learnColumn = 'Learn More';
                    	$(".sw-channel-dropdown .mega-columns .mega-menu-right", this).append(learnColumn);
                    }

                    // ADD IMAGE
                    var imgColumn = '';
                    var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                        imgColumn = '';
                    }
                    $(".sw-channel-dropdown .mega-columns .mega-menu-right", this).prepend(imgColumn);

                    //ADD TRIANGLE INDICATOR
                    var hoverTriangle = '
';
                    $(this).append(hoverTriangle);

                }
            });
