        "Search": function() {
            //CUSTOM MYSTART SEARCH PLACEHOLDER
            var defText = "Type and press Enter to search our website.";
            $(".custom-search").removeAttr('placeholder');
            $(".custom-search").attr("value", defText);
            $(".custom-search").focus(function() {
                if($(this).val() == defText) {
                    $(this).val("");
                }
            });
            $(".custom-search").blur(function() {
                if($(this).val() == "") {
                    $(this).val(defText);
                }
            });

            $('.custom-search').keyup(function(e) {        
                if (e.keyCode == 13) {
                    window.location.href="//" + window.location.hostname + "/site/Default.aspx?PageType=6&SiteID=" + 1 + "&SearchString=" + $(this).val();
                }	
            });

            var defText = "Type and press Enter to search our website.";
            $(".custom-search-extra").removeAttr('placeholder');
            $(".custom-search-extra").attr("value", defText);
            $(".custom-search-extra").focus(function() {
                if($(this).val() == defText) {
                    $(this).val("");
                }
            });
            $(".custom-search-extra").blur(function() {
                if($(this).val() == "") {
                    $(this).val(defText);
                }
            });

            $('.custom-search-extra').keyup(function(e) {        
                if (e.keyCode == 13) {
                    window.location.href="//" + window.location.hostname + "/site/Default.aspx?PageType=6&SiteID=" + 1 + "&SearchString=" + $(this).val();
                }	
            });

            $("#gb-search-btn").click(function(e){
            e.preventDefault();
            animateSearch();
            });
            $("#gb-search-btn").keydown(function(e){
                if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                    e.preventDefault();
                    animateSearch();
                }
            });
            var searchTimer = 0;
            $("#gb-search-container").mouseenter(function() {
                clearTimeout(searchTimer);
            });
            $("#gb-search-container").mouseleave(function() {
                searchTimer = setTimeout(function(){
                    $(".custom-search").animate({
                        width: "0px"
                    }, 500, function() {
                        $("#gb-search-btn").attr("aria-label", "Open search").focus();
                        $("#gb-search-container").removeClass("open").attr("aria-hidden", "true");
                        $(".custom-search").removeClass("open").attr("aria-hidden", "true");
                        $("#cs-custom-search-input").attr("tabindex","-1");
                    });
                }, 1000);
            });

            //FOR STICKY SEARCH
            $("#gb-search-btn-extra").click(function(e){
            e.preventDefault();
            animateSearchExtra();
            });
            $("#gb-search-btn-extra").keydown(function(e){
                if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                    e.preventDefault();
                    animateSearchExtra();
                }
            });
            var searchTimer = 0;
            $("#gb-search-container-extra").mouseenter(function() {
                clearTimeout(searchTimer);
            });
            $("#gb-search-container-extra").mouseleave(function() {
                searchTimer = setTimeout(function(){
                    $(".custom-search-extra").animate({
                        width: "0px"
                    }, 500, function() {
                        $("#gb-search-btn-extra").attr("aria-label", "Open search").focus();
                        $("#gb-search-container-extra").removeClass("open").attr("aria-hidden", "true");
                        $(".custom-search-extra").removeClass("open").attr("aria-hidden", "true");
                        $("#cs-custom-search-input-extra").attr("tabindex","-1");
                    });
                }, 1000);
            });

            function animateSearch() {
                if(!$("#gb-search-container").hasClass("open")) {
                    $("#gb-search-btn").attr("aria-label", "Close search");
                    $("#gb-search-container").addClass("open").attr("aria-hidden", "false");
                    $(".custom-search").addClass("open").attr("aria-hidden", "false");
                    $("#cs-custom-search-input").attr("tabindex","0");

                    $(".custom-search").animate({
                        width: "300px"
                    }, 500, function() {
                        $(".custom-search").focus();
                    });
                } else {
                    $(".custom-search").animate({
                        width: "0px"
                    }, 500, function() {
                        $("#gb-search-btn").attr("aria-label", "Open search").focus();
                        $("#gb-search-container").removeClass("open").attr("aria-hidden", "true");
                        $(".custom-search").removeClass("open").attr("aria-hidden", "true");
                        $("#cs-custom-search-input").attr("tabindex","-1");
                    });
                }
            }

            function animateSearchExtra() {
                if(!$("#gb-search-container-extra").hasClass("open")) {
                    $("#gb-search-btn-extra").attr("aria-label", "Close search");
                    $("#gb-search-container-extra").addClass("open").attr("aria-hidden", "false");
                    $(".custom-search-extra").addClass("open").attr("aria-hidden", "false");
                    $("#cs-custom-search-input-extra").attr("tabindex","0");

                    $(".custom-search-extra").animate({
                        width: "300px"
                    }, 500, function() {
                        $(".custom-search-extra").focus();
                    });
                } else {
                    $(".custom-search-extra").animate({
                        width: "0px"
                    }, 500, function() {
                        $("#gb-search-btn-extra").attr("aria-label", "Open search").focus();
                        $("#gb-search-container-extra").removeClass("open").attr("aria-hidden", "true");
                        $(".custom-search-extra").removeClass("open").attr("aria-hidden", "true");
                        $("#cs-custom-search-input-extra").attr("tabindex","-1");
                    });
                }
            }

