            	//Move all of the titles to the top of the card
				$(this).find(".ui-article-title").prependTo($(this).find('.ui-article'));
            });
            //kick the more link out of the ul and into the uls parent
			$('.hp.column.left .ui-widget.app.headlines a.more-link').appendTo($('.app.headlines .ui-widget-detail'));
            $('.sp .ui-widget.app.headlines a.more-link').appendTo($('.app.headlines .ui-widget-detail'));

        }
