	    $(document).ready(function () {
	        // handles news and announcements
	        $('div#Announcements #Header').click(function () {
	            ToggleNewsList();
	        });
	    });

	    // does the actual toggling (hide/show) of the news items
	    function ToggleNewsList() {
	        $('#NewsList').slideToggle('slow');
	        $('#NewsScroller').toggle();
	        if ($('div#Announcements #Header').hasClass('expanded')) {
	            // remove class
	            $('div#Announcements #Header').removeClass('expanded');
	        }
	        else {
	            // add class
	            $('div#Announcements #Header').addClass('expanded');
	        }
	    }

