link.script:
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        layout: 0
    }, 'google_translate_element');
}

div.script:
        //onscroll fix header height
        $(window).scroll(reduceHeader);

        //Initial load of page
        $(document).ready(sizeContent);
        //Every resize of window
        $(window).resize(sizeContent);
        //Dynamically assign height
        function sizeContent() {
            var newHeight = $(".inner-right-col").height();
            $(".inner-left-col").css("min-height", newHeight + 40);
            reduceHeader();
            smoothScroll($(".header-fixed-wrap").height());
        }
        //reduce header height
        function reduceHeader() {
            if ($(window).scrollTop() > 200) {
                $(".header-fixed-wrap").addClass("reduced");
            }
            else {
                $(".header-fixed-wrap").removeClass("reduced");
            };
        }

        $(function () {
            $('.top-btn').click(function (e) {
                e.preventDefault();
                $('html, body').animate({ scrollTop: '0px' }, 300);
            });
        });

        function smoothScroll(offset) {
            var source = $('a[href^="#"]').not('.top-btn');
            source.on('click', function (event) {
                var target = $($(this).attr('href'));

                if (target.length) {
                    event.preventDefault();
                    $('html, body').animate({
                        scrollTop: target.offset().top - offset
                    }, 500);
                }
            });
        }

    var _gaq = _gaq || [];
    _gaq.push(['_setAccount', 'UA-125190701-1']);
    _gaq.push(['_trackPageview']);

    (function() {
        var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;
        ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';
        var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);
    })();
