div.script:
        //

        $(document).ready(function() {
	var settings = $("#PHP_settings_BLOCK_1586").val();
	settings = JSON.parse(settings);

	var googleCalendarApiKey = "AIzaSyDOQDk0rQBxEigRKnBCcqaOYYTBu_LMge4";
	var googleCalendarId = "arsnc.org_sfkgd16fbdr5ef8dmnohefjs0s@group.calendar.google.com";
	var listCount = "5";
	var events = [];
	var momentLang = 'en-gb';
	var now = moment().format("YYYY-MM-DDTHH:mm:ss") + 'Z';

	generateList();

	function generateList() {
		$.get('https://www.googleapis.com/calendar/v3/calendars/' +
			googleCalendarId +
			'/events?key=' +
			googleCalendarApiKey +
			'&maxResults=' +
			listCount +
			'&orderBy=startTime&singleEvents=true&timeMin=' +
			now, function(data) {
			events = data.items;

			if (events.length > 0) {
				var itemsHtml = "";
				events.forEach(function(e) {
					itemsHtml += generateItemList(e);
				});

				$("#dsEventCalendarBLOCK_1586 .dsListEvents ul").html(itemsHtml);
			} else {
				$("#dsEventCalendarBLOCK_1586 .dsListEvents").html(settings.noEventsText);
			}
		}).fail(function(err) {
			var error = JSON.parse(err.responseText).error;
			var errors = "Google Calendar Error: ";
			errors += error.message;
			errors += '
If you see this error please : ' +
				'check your Google Calendar ID ' +
				', ' +
				'API key' +
				' or ' +
				'disable any email deobfuscator addons' +
				'';

			$("#dsEventCalendarBLOCK_1586 .dsErrorInfo")
				.html(errors)
				.css("display", "block");
		});
	}

	function generateItemList(event) {
		var start, end, start_day, end_day;

		if ('dateTime' in event.start) {
			start = event.start.dateTime;
			start_day = '' + moment(start).lang(momentLang).format(settings.timeFormat) + ''
				+ " " + '' + moment(start).lang(momentLang).format(settings.formatEvent) + '';
		}

		if ('date' in event.start) {
			start = event.start.date;
			start_day = '' + moment(start).lang(momentLang).format(settings.formatEvent) + "";
		}

		if ('dateTime' in event.end) {
			end = event.end.dateTime;
			end_day = '' + moment(end).lang(momentLang).format(settings.timeFormat) + ''
				+ " " + '' + moment(end).lang(momentLang).format(settings.formatEvent) + '';
		}

		if ('date' in event.end) {
			end = event.end.date;
			end_day = '' + moment(end).lang(momentLang).format(settings.formatEvent) + "";
		}

		var time = "";
		time += start_day;
		if ('dateTime' in event.start) {
			time += ' - ' + end_day;
		}


		var eventHtml = "";
		eventHtml += '
';
		eventHtml += '
' + (event.summary || '') + '';

		eventHtml += '
' + time + '';

		eventHtml += '
';
		eventHtml += '' + event.id + '';
		eventHtml += '' + event.summary + '';
		eventHtml += '' + start_day + '';
		eventHtml += '' + end_day + '';
		eventHtml += '' + event.htmlLink + '';
		eventHtml += '' + (event.description || ' ') + '';
		eventHtml += '';
		eventHtml += '';
		return eventHtml;
	}

	var modal = $("#dsEventModalBLOCK_1586");
	$("#dsEventCalendarBLOCK_1586 .dsListEvents ul").on('click', 'li', function() {
		var that = this;
		var start_day = $(that).find(".start_time").text();
		var end_day = $(that).find(".end_time").text();
		var title = $(that).find(".title").text();
		var description = $(that).find(".description").html();

		modal.find('.header .title').text(title);
		modal.find('.content .time').text(start_day + ' - ' + end_day);
		modal.find('.content .description').html(description);
		modal.find('.header, .footer').css("border-color", settings.color);
		modal.addClass('active');

	});

	modal.on('click', '.btn.btn-close', function() {
		modal.removeClass('active');
	})
});
