div.script:
	    	var step = 1;
		 	var rows = 2;
		 	var timer;
            var pause;

		 	function slideit()
		 	{
		 		if (step==1)
		 		{ 			
		 			$("#slide" + step).show("slow");
		 			if (rows>2){
		 				$("#slide" + (rows-1)).hide("slow");
		 				// $("div.social").removeClass("active");
		 			} 
		 		}else{ 					
			 		$("#slide" + step).show("slow");
			 		$("#slide" + (step-1)).hide("slow");
			 		// $("div.social").removeClass("active");	 		
		 		}
		 		if (step < (rows-1)) {
		 			step++;
		 		}else step = 1;

		 		timer=setTimeout("slideit()",10000);
		 	}
			slideit();


		 	var stepd = 1;
		 	var rowsd = 1;
		 	var timerd;
            var paused;
		 	function slideitd()
		 	{ 		
		 		if (stepd==1)
		 		{ 			
		 			$("#slided" + stepd).show("slow");
		 			if (rowsd>2) $("#slided" + (rowsd-1)).hide("slow");
		 			// $("div.social").removeClass("active");
		 		}else{ 					
			 		$("#slided" + stepd).show("slow");
			 		$("#slided" + (stepd-1)).hide("slow");
			 		// $("div.social").removeClass("active");	 		
		 		}
		 		if (stepd < (rowsd-1)) {
		 			stepd++;
		 		}else stepd = 1;

		 		timerd=setTimeout("slideitd()",10000);
		 	}
		 	slideitd();

		 	function stop(){
                clearTimeout(timer);
                pause=1;
            }

            function stop_d(){
                clearTimeout(timerd);
                paused=1;
            }

