	function stickyHeader() {
		//DOC vars
		scrolled = $(window).scrollTop();
		loc = $("#gb-channel-list-outer").outerHeight(true);

		if(scrolled >= loc+125  && scrollLock == 0) {
			$("#gb-channel-list-outer, #gb-header-outer").hide();
			$("#gb-channel-list-outer").addClass("fixed-header");
			$("#gb-header-outer").addClass("fixed-header").css("top", loc+"px");
			$("#gb-search-outer").addClass("fixed-header").css("top", 60+loc+"px");
			$("#gb-popular-links-outer").addClass("fixed-header").css("top", 200+loc+"px");
			//$("#gb-school-list-outer").addClass("fixed-header").css("top", 60+loc+"px");
			$("body").css("padding", loc+"px 0 0 0");
			$("#gb-channel-list-outer, #gb-header-outer").fadeIn(500);
			scrollLock = 1;
		} else if(scrolled < loc+125  && scrollLock == 1){
			if($(".sp").length) {
				$("#gb-channel-list-outer").removeClass("fixed-header");
				$("#gb-header-outer").removeClass("fixed-header").css("top", 0);
				$("#gb-search-outer").removeClass("fixed-header").css("top", "180px");
				$("#gb-popular-links-outer").removeClass("fixed-header").css("top", "300px");
				$("#gb-school-list-outer").css("top", "180px");
				$("body").css("padding", "0");
				scrollLock = 0;			
			} else {
				$("#gb-channel-list-outer").removeClass("fixed-header");
				$("#gb-header-outer").removeClass("fixed-header").css("top", 0);
				$("#gb-search-outer").removeClass("fixed-header").css("top", "120px");
				$("#gb-popular-links-outer").removeClass("fixed-header").css("top", "240px");
				$("#gb-school-list-outer").css("top", "120px");
				$("body").css("padding", "0");
				scrollLock = 0;
			}
		}
	}

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				if($(".hp").length){
					photoGallery();
				}
				if(!$("#cs-sw-footer #sw-mystart-mypasskey").length) {
					$("#cs-sw-footer #sw-mystart-mypasskey").appendTo($("#cs-sw-footer"));
				}
			break;
			case "768":
				if($(".hp").length){
					photoGallery();
				}
				if($("#cs-sw-footer #sw-mystart-mypasskey").length) {
					$("#gb-footer-icons .gb-site-map").after($("#cs-sw-footer #sw-mystart-mypasskey"));
				}
			break;
			case "640":
				if($(".hp").length){
					photoGallery();
				}
				if($("#cs-sw-footer #sw-mystart-mypasskey").length) {
					$(".gb-footer-info-640 .gb-site-map").after($("#cs-sw-footer #sw-mystart-mypasskey"));
				}
			break;
			case "480":
				if($(".hp").length){
					photoGallery();
				}
				if($("#cs-sw-footer #sw-mystart-mypasskey").length) {
					$(".gb-footer-info-640 .gb-site-map").after($("#cs-sw-footer #sw-mystart-mypasskey"));
				}
			break;
			case "320":
				if($(".hp").length){
					photoGallery();
				}
				if($("#cs-sw-footer #sw-mystart-mypasskey").length) {
					$(".gb-footer-info-320 .gb-site-map").after($("#cs-sw-footer #sw-mystart-mypasskey"));
				}
			break;
		}
	}	

