	function setActiveSection() {
		if($(".sp").length) {
			//ADD CLASS ACTIVE TO ACTIVE SECTION LINK
			var activeSection = "";
			$("#section-navigation li > a span").each(function() {
				var getText = $(this).text();

				if(getText == activeSection) {
					$(this).parent().parent().addClass("active");
				}
			});

			//SETUP PAGE NAV LINKS UNDER SECTION LINK
			$("
").appendTo("#section-navigation li");
			$(".page-navigation > li").each(function() {
				$(this).appendTo("#section-navigation li.active .cs-section-pages");
			});

			var numSections = $("#section-navigation li.active .cs-section-pages li").size();
			if(numSections < 2) {
				$("#section-navigation li.active .cs-section-pages").hide();
				$("div.ui-widget.app.navigation.sectionnavigation li.active").addClass("no-pages");
			}
		}
	}

	function getExtraMenuOptions() {
		var extraItems = "";
		$(".gb-school-list-header").each(function() {
			extraItems += $("span", this).text();
			if($(this).index() != $(".gb-school-list-header").length-1) {
				extraItems += ";";
			}
		});
		return extraItems;
	}

	function getExtraMenuItems() {
		var extraItems = "";
		$(".gb-school-list-section").each(function() {
			var extraMenuItems = "";
			var element = $(this);
			$(".gb-school-list-column a", this).each(function() {
				extraMenuItems += $(element).index()+1+","+$(this).text()+","+$(this).attr("href")+",_self;";
			});
			extraItems += extraMenuItems;
		});
		return extraItems;
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

	function schoolListEvents() {
		// CLICK FUNCTION FOR SCHOOL DROP-DOWN
		$(".global-icon.schools-icon").on("click", function(e) {
			e.preventDefault();
			$("body, #gb-header-outer, #gb-channel-list-outer").toggleClass("overlay");
			$(".global-icon.schools-icon").toggleClass("opened");
			if(scrollLock == 1) {
				$("#gb-school-list-outer").css("top", loc+scrolled+"px");
			} else {

			}
        		if(!$("#gb-school-list-outer").hasClass("open")){
	        		$("#gb-school-list-outer").removeClass("closed").addClass("open");
        		} else {
	        		$("#gb-school-list-outer").removeClass("open").addClass("closed");
        		}
        	});

		// TAB HEADER CLICK
		$(".gb-school-list-header").click(function(e){
			e.preventDefault();
			// IF THE SELECTED TAB ISNT ALREADY ACTIVE
			if(!$(this).hasClass("active")){
				// GET CLASS OF SELECTED TAB
				var selectedList = $(this).attr("class");
				selectedList = jQuery.trim(selectedList.replace("gb-school-list-header ", ""));

				//REMOVE CURRENT ACTIVE CLASSES
				$(".gb-school-list-header.active, .gb-school-list-section.active").removeClass("active");

				// SET NEW ACTIVE CLASSES
				$(this).addClass("active");
				$(".gb-school-list-section." + selectedList).addClass("active");
			}
		});
	}

