					sliderNode = lastChild.cloneNode(true);
					sliderNode.setAttribute("id", "ir6067-sliderContainer");
					sliderNode.style.display = "block";
				}
				container.removeChild(lastChild);
			}

			if(sliderNode) {
				container.appendChild(sliderNode);
