	function linkSlider() {
		//SLIDER UNDER RESOURCE DROPDOWN LINKS
		if ($("#gb-page.sp").length || $("#gb-page.hp").length) {
			var $el, leftPos, newWidth,
			$mainNav = $("#slide-wrapper");

			var $magicLine = $(".current-link");

			/*$magicLine
				.width($("#slide-wrapper a").width()/2)
				.css("left", $("#slide-wrapper a").position().left+30)
				.data("origLeft", $magicLine.position().left)
				.data("origWidth", $magicLine.width());*/

			$("#slide-wrapper a").hover(function() {
				$el = $(this);
				leftPos = $el.position().left;
				newWidth = $el.width();
				$(".onHover").removeClass("onHover");
				$(this).addClass("onHover");
				$magicLine.stop().animate({
					left: leftPos+25,
					width: newWidth/2
				});
			}, function() {
				$magicLine.stop().animate({
					left: $magicLine.data("origLeft"),
					width: $magicLine.data("origWidth")
				});  
			});
		}
	}

