} catch(e) {
	if(window.console)
		console.log(e);
}

function resize_sp1203() {
	var windowWidth = Math.max(parseFloat("600"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("1170");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel1203Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel1203Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel1203Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel1203Div").style.height = Math.max((parseFloat("360")/parseFloat("600") * windowWidth), parseFloat("360")) + "px";
	document.getElementById("carousel1203Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel1203Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

img.script:
(function(scripts) {

	for (var i = 0; i < scripts.length; i++) {
		var script = scripts[i];
		if (script.src && script.src.match("wotd2.js")) {
			try {
				var newScript = document.createElement("script");
				var cbName = "cb1611";
				window[cbName] = function(html) {
					newScript.outerHTML = html;
				}
				newScript.src = "/apps/word_of_the_day/hp_word_of_the_day.jsp?cb=" + cbName;
				script.parentNode.replaceChild(newScript, script);
			} catch(e) {
				console.log(e);
			}
			break;
		}
	}

})(document.getElementsByTagName("script"));

link.script:
var curWindowOnLoad = window.onload;
window.onload = function() {
	if(curWindowOnLoad) {
		curWindowOnLoad();
	}
	if(document.getElementsByClassName) {
		var newsImages = document.getElementsByClassName("news-img");
		for(i=0; i<newsImages.length; ++i) {
			newsImages[i].parentNode.style.marginRight = "1px";
		}
		var hpImages = document.getElementsByClassName("hp-img");
		for(i=0; i<hpImages.length; ++i) {
			hpImages[i].parentNode.style.marginRight = "1px";
		}
	}
};
