	function jsQueries(){
		var getWidth = jsBreakPoint();
		if (getWidth == 960) { // 960
			/*megaDropdownResponsive();*/
			channelMod();
		} if (getWidth == 768) { // 768
			channelMod();
		} if (getWidth == 640) { // 640

		} if (getWidth == 480) { // 480
			appView();
		} if (getWidth == 320) { // 320
			appView();
		}
	}

	function photoGallery(){
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
				var imgW = ("wcpss" == "wcpss") ? 1565 : 870;
				var imgH = ("wcpss" == "wcpss") ? 600 : 470;
				var bulletSwitch = ("wcpss" == "wcpss") ? "yes" : "no";
                var arrowSwitch = ("wcpss" == "wcpss") ? "no" : "yes";

				// MULTIMEDIA ROTATOR
				$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
					"imageWidth"              : imgW,
					"imageHeight"             : imgH,
					"appHeader"               : "no",
					"titles"                  : "yes",
					"captions"                : "yes",
					"playPause"               : "no",
					"mainControls"            : arrowSwitch,
					"contMoreLink"            : "yes",
					"contMoreLinkText"        : "LEARN MORE",
					"descMoreLink"            : "no",
					"descMoreLinkText"        : "Read More",
					"bullets"                 : bulletSwitch,
					"bulletControls"          : "no",
			 		"thumbnails"              : "no",
					"thumbnailWidth"          : 122,
					"thumbnailHeight"         : 53,
					"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
					"hoverPause"              : "yes",
			                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
					"fullScreenVideo"         : "yes",
					"transitionType"          : "fade", // fade or slide
					"transitionSpeed"         : 3,
					"transitionDelay"         : 8,
					"onImageLoad"             : function() { },
					"allImagesLoaded"         : function() { },
					"onThumbnailLoad"         : function() { },
					"allThumbnailsLoaded"     : function() { },
					"onTransitionStart"       : function() { },
					"onTransitionEnd"         : function() { },
					"watchVidClick"           : function() { },
					"closeVidClick"           : function() { },
					"allLoaded"               : function() {
						$("#hp-slideshow .rs-gallery-bullets-outer").insertAfter("#hp-slideshow .rs-gallery-desc");
					}
				});
			}
		});
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content-top div.ui-widget.app.active").length){
			$("#hp-content-top div.ui-widget.app:first").addClass("active");
		}
		if(!$(".hp.column.four div.ui-widget.app.active").length){
			$(".hp.column.four div.ui-widget.app:first").addClass("active");
		}
		if(!$(".hp.column.five div.ui-widget.app.active").length){
			$(".hp.column.five div.ui-widget.app:first").addClass("active");
		}

		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-top div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});
		$(".hp.column.four div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});
		$(".hp.column.five div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
		$("#hp-content-top div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content-top div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
		$(".hp.column.four div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$(".hp.column.four div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
		$(".hp.column.five div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$(".hp.column.five div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function subpageAccordion(){
		if($(".region.cs-accordion").length) {
			//DOC make homepage apps collapsible for small mobile devices
			if(!$(".region.cs-accordion div.ui-widget.app.cs-accordion-item.active").length){
				$(".region.cs-accordion div.ui-widget.app.cs-accordion-item:first").addClass("active");
			}

			//DOC accordion header click
			$(".region.cs-accordion div.ui-widget.app.cs-accordion-item div.ui-widget-header").click(function(){
				var clickedApp = $(this).parent().parent().attr("id");

				$(".region.cs-accordion div.ui-widget.app.cs-accordion-item").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			});
		}
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(jQuery.trim($("#sp-section-name h1").text()) == "") {
			if("" == "") {
				$("#sp-section-name h1").append(jQuery.trim($("li.sw-channel-item.active:eq(0) > a span").text()));
			} else {
				//$("#sp-section-name h1").append("");
			}
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");

		//DOC move mystart buttons and dropdowns

			if($(".sw-mystart-button.register").length){
				$(".gb.mystart.right").append($(".sw-mystart-button.register"));
			}
			$(".sw-mystart-button.signin span").text("Admin");
			$(".gb.mystart.right").append($(".sw-mystart-button.signin"));


		// ********** HEADER ********** //
		// REMOVE ALL APPS EXCEPT FOR ANNOUNCEMENTS APP AND ALERT APP IN UPDATE REGION
		$("#hp-update .ui-widget.app").not($("#hp-update .ui-widget.app.announcements")).not($("#hp-update .ui-widget.app.cs-alert-notification")).each(function() {
			$(this).remove();
		});

		// REMOVE ALL APPS EXCEPT FOR ANNOUNCEMENTS APP AND ALERT APP IN ALERT REGION
		$("#hp-alert .ui-widget.app").not($("#hp-alert .ui-widget.app.announcements")).not($("#hp-alert .ui-widget.app.cs-alert-notification")).each(function() {
			$(this).remove();
		});

		// SHOW UPDATE AND/OR ALERT IF NEEDED
        	if($(".hp").length){
        		if(($("#hp-update .ui-widget.app.announcements").length && $("#hp-update .ui-widget.app.announcements .ui-articles li").length) || ($("#hp-update .ui-widget.app.cs-alert-notification").length && !$("#hp-update .ui-widget.app.cs-alert-notification").hasClass("hidden"))){
        			$("#hp-update-outer").removeClass("hidden");
        		}
        		if(($("#hp-alert .ui-widget.app.announcements").length && $("#hp-alert .ui-widget.app.announcements .ui-articles li").length) || ($("#hp-alert .ui-widget.app.cs-alert-notification").length && !$("#hp-alert .ui-widget.app.cs-alert-notification").hasClass("hidden"))){
        			$("#hp-alert-outer").removeClass("hidden");
        		}
        	}

		//DOC add logo
		var logoCheck = "true";
		if(logoCheck == "true") {
			var logoSrc = jQuery.trim("/cms/lib/NC01911451/Centricity/Template/GlobalAssets/images///logos/district.png");
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if(logoSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
				$("#gb-logo").html('').removeClass("hidden");
			}
		}

		//DOC add sitename
		var siteNameOne = jQuery.trim("");
		var siteNameTwo = jQuery.trim("");
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Wake County Public School System";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		//DOC add tagline
		if((jQuery.trim("") != "") && ("wcpss" != "wcpss")) {
			$("#gb-sitename").append("
");
		}

		//DOC add extra text if not on district
		 var headerText = "";
		 if("wcpss" != "wcpss") {
			var text = ["", "", "", ""];

			for(var i in text) {
				if(jQuery.trim(text[i]) != ""){
					headerText += "
" + text[i] + "";
				}
			}
		} else {
        	if($.trim("Staff Intranet") != "") {
            	headerText += '
Staff Intranet';
            }
            if($.trim("Our Strategic Plan") != "") {
            	headerText += '
Our Strategic Plan';
            }
        }
        $("#gb-header-text").html(headerText);

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
