	function buildStyleSheet(){
		var channelDropdownColor = "rgba(" + hexToRgb('#860038') + ", 0.75)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles = '.wcpss #gb-channel-list ul.sw-channel-dropdown li.mega-channel-bg-overlay {' +
						'background: ' + channelDropdownColor + ' !important;' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function channelMod() {
		// RESET THE MARGIN
	        $("li.sw-channel-item > a span").css("margin", "0px");

	        // SET UP OUR VARIABLES
	        var headerChannelNum = $("#gb-channel-list li.sw-channel-item").length, headerChannelWidths = 0, headerChannelBarWidth = $("#gb-channel-list .sw-channel-list").width() - 1, headerAvailSpace = 0, headerChannelPad = 0;
	        var footerChannelNum = $("#gb-footer-channel-list li.sw-channel-item").length, footerChannelWidths = 0, footerChannelBarWidth = $("#gb-footer-channel-list .sw-channel-list").width() - 1, footerAvailSpace = 0, footerChannelPad = 0;

	        // GET THE TOTAL WIDTH OF ALL THE HEADER CHANNELS
	        $("#gb-channel-list li.sw-channel-item").each(function(){
	                headerChannelWidths += $(this).outerWidth(true);
	 	});

	        // GET THE TOTAL WIDTH OF ALL THE FOOTER CHANNELS
	        $("#gb-footer-channel-list li.sw-channel-item").each(function(){
	                footerChannelWidths += $(this).outerWidth(true);
	        });

	        // GET THE TOTAL AMOUNT OF EMPTY SPACE LEFT OVER FOR HEADER CHANNELS
	        headerAvailSpace = headerChannelBarWidth - headerChannelWidths;

	        // GET THE TOTAL AMOUNT OF EMPTY SPACE LEFT OVER FOR FOOTER CHANNELS
	        footerAvailSpace = footerChannelBarWidth - footerChannelWidths;

	        // GET THE AMOUNT OF SPACE TO SET BETWEEN EACH HEADER CHANNEL
	        // SUBTRACT ONE FROM THE TOTAL CHANNEL NUMBER BECAUSE WE WANT THE LAST CHANNEL TO SIT AGAINST THE RIGHT EDGE OF THE TEMPLATE
	        headerChannelPad = Math.floor(headerAvailSpace / (headerChannelNum - 1));

	        // GET THE AMOUNT OF SPACE TO SET BETWEEN EACH FOOTER CHANNEL
	        // SUBTRACT ONE FROM THE TOTAL CHANNEL NUMBER BECAUSE WE WANT THE LAST CHANNEL TO SIT AGAINST THE RIGHT EDGE OF THE TEMPLATE
	        footerChannelPad = Math.floor(footerAvailSpace / (footerChannelNum - 1));

	        // NOW APPLY THE MARGIN TO THE HEADER CHANNELS
	        $("#gb-channel-list li.sw-channel-item > a span").not($("#gb-channel-list li.sw-channel-item:last > a span")).css("margin", "0px " + Math.floor(headerChannelPad / 2) + "px");

	        // NOW APPLY THE MARGIN TO THE FOOTER CHANNELS
	        $("#gb-footer-channel-list li.sw-channel-item > a span").not($("#gb-footer-channel-list li.sw-channel-item:last > a span")).css("margin", "0px " + Math.floor(footerChannelPad / 2) + "px");
	}

	function socialMediaIcons() {
		// SET UP OUR VARIABLES AND DATA
		var socialMedia = "";
        	var socialIcons = [
        		["facebook", "true", "https://www.facebook.com/WCPSS", "f"],
        		["twitter", "true", "https://twitter.com/WCPSS", "t"],
        		["youtube", "true", "https://www.youtube.com/user/WakeCountySchools", "y"],
        		["instagram", "true", "http://instagram.com/wakeschools/", "I"],
        		["pinterest", "false", "#", "p"],
        		["email", "false", "#", "e"],
        		["rss", "true", "http://j.mp/1snbWO0", "R"],
        		["linkedin", "true", "https://www.linkedin.com/company/wake-county-public-school-system", "i"],
        		["vimeo", "false", "#", "v"],
        		["google-plus", "true", "https://plus.google.com/109419000772587111394/photos", "g"]
        	]

        	// GRAB THE ACTIVE ICONS
        	for(var i = 0; i < socialIcons.length; i++) {
        		if(socialIcons[i][1] == "true") {
        			socialMedia += "" + socialIcons[i][3] + "";
        		}
        	}

        	// ADD TO HEADER AND FOOTER IF ON DISTRICT
        	if("wcpss" == "wcpss") {
        		$("#gb-header-social-icons, #gb-footer-social-icons").append(socialMedia);
        		$("#gb-header-social-icons .gb-social-icon:first, #gb-footer-social-icons .gb-social-icon:first").addClass("first");
        		$("#gb-header-social-icons .gb-social-icon:last, #gb-footer-social-icons .gb-social-icon:last").addClass("last");
        	}

        	// ADD TO FOOTER ONLY IF ON SCHOOL SITES
        	else {
        		$("#gb-footer-social-icons").append(socialMedia);
        		$("#gb-footer-social-icons .gb-social-icon:first").addClass("first");
        		$("#gb-footer-social-icons .gb-social-icon:last").addClass("last");

        		if(!$("#gb-footer-social-icons a").length) {
        			$("#gb-footer-social-icons h1").hide();
        			$("#gb-footer-bottom").addClass("no-icons");
        		}
        	}
	}

	function globalIcons() {
		if($(".hp").length) {
			// SET UP OUR VARIABLES AND DATA
			var gbIcons = "";
		        var globalIcons = [
					["transportation", "true", "https://www.wcpss.net/domain/30", "T"],
					["school meals", "true", "https://www.wcpss.net/domain/1047", ""],
					["calendar", "true", "https://www.wcpss.net/domain/19", "c"],
					["enrollment", "true", "https://www.wcpss.net/domain/13656", "E"],
					["home base", "true", "https://www.wcpss.net/domain/69", "H"],
					["blackboard", "false", "http://blackboard.wcpss.net/webapps/portal/frameset.jsp", "b"],
					["webmail", "false", "#", "w"],
					["google apps", "false", "#", "G"],
					["digital resources", "true", "http://wcpss.net/Page/2043", ""],
					["pta/ptsa", "false", "#", ""],
					["athletics", "false", "#", "a"],
					["jobs", "true", "https://www.wcpss.net/domain/15", "J"],
					["principal's message", "false", "#", "P"],
					["peach jar", "false", "#", "j"],
					["wakeid", "false", "#", ""]
		        ]

		        // GRAB THE ACTIVE ICONS
		        var iconClass = ["one", "two", "three", "four", "five" , "six", "seven"];
                var iconName = "";
                for(var i = 0, n = 0; i < globalIcons.length; i++) {
                    // GRAB ONLY THE FIRST 7 ICONS
                    if(n < 7) {
                        if(globalIcons[i][1] == "true") {
							iconName = globalIcons[i][0].replace(/\s/g, "-").replace("/", "-");
                            gbIcons += "";
                            gbIcons +=	"";
                            gbIcons +=		"" + globalIcons[i][3];
                            if(globalIcons[i][0] == "wakeid") {
                            	gbIcons +=		"";
                            }
                            gbIcons +=		"";
                            gbIcons +=		"" + globalIcons[i][0] + "";
                            gbIcons +=	"";
                            gbIcons += "";
                            n++;
                        }
                    }
                }

                // ADD CLASS TO OUTER CONTAINER
                var iconNum = ["hp-none", "hp-one", "hp-two", "hp-three", "hp-four", "hp-five" , "hp-six", "hp-seven"];
                $("#hp-global-icons-outer").addClass(iconNum[n]);

                // ADD THE ICONS TO THE DOM
                $("#hp-global-icons").addClass(iconNum[n]).html(gbIcons);
		}
	}

