	function searchText() {
		$(".gb-search-button").click(function(e){
		    e.preventDefault();
		    $(this).parent().submit();
		});
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "find it fast") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("find it fast");
		    }
		});

        $(".gb-search-form").submit(function(e) {
        	e.preventDefault();

            if($.trim($(".gb-search-input", this).val()) != "" && $.trim($(".gb-search-input", this).val()) != "find it fast") {
            	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", this).val();
            }
        });

        $("#gb-channel-list ul.sw-channel-list li.sw-channel-item.search a").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.space || e.keyCode == csKeyCodes.enter) {
            	e.preventDefault();

                $("#gb-search").toggle().attr("aria-hidden", ($("#gb-search").attr("aria-hidden") == "true") ? "false" : "true");
                if($("#gb-search").attr("aria-hidden") == "true") {
                	$("#gb-search").removeAttr("style");
                } else {
                	$(".gb-search-input").focus();
                }
            }
            if(e.shiftKey && e.keyCode == csKeyCodes.tab) {
            	$("#gb-search").hide().attr("aria-hidden", "true").removeAttr("style");
            }
        });

        $(".gb-search-button").keydown(function(e){
		    if(e.keyCode == csKeyCodes.space || e.keyCode == csKeyCodes.enter) {
            	e.preventDefault();

                $(this).parent().submit();
            }
            if(e.keyCode == csKeyCodes.tab) {
            	$("#gb-search").hide().attr("aria-hidden", "true").removeAttr("style");
            }
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			// LIMIT EVENTS ON HOMEPAGE IF ON DISTRICT
	        	/* Removed by Jeremy K 20150129 to not limit events on district home 
	        	if("wcpss" == "wcpss") {
	        		$("#hp-content-top .upcomingevents li:gt(4)").remove();
	        	}
			*/

			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "yes",
					dayWeek     : "yes"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			}

			if(parseInt($(".joel-date", this).text()) < 10) {
				$(".joel-date", this).prepend("0");
			}
		});

		// ADD EVENTS CAPTIONS
		var eventsCaption = "true";
		if(eventsCaption == "true") {
			$("#hp-content-events .upcomingevents").addClass("events-caption");
		}

		$("#hp-content-events .upcomingevents .ui-articles").wrap("
");
		$("#hp-content-events .upcomingevents li").first().addClass("first");
		$("#hp-content-events .upcomingevents li").eq(4).addClass("last");

			/* Added by Jeremy K 20150129 to limit events in horizontal view*/
		$("#hp-content-events .upcomingevents li:gt(4)").remove();

		// ADD CLASSES TO ARTICLES
		var eventClass = ["one", "two", "three", "four", "five"];
		$("#hp-content-events .upcomingevents li").each(function() {
			var thisIndex = $(this).index();
			$(this).addClass("event-" + eventClass[thisIndex]);
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = jsBreakPoint();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	function jsBreakPoint(){
		// 960
		if ($(window).width() >= 1024) { return 960; } 

		// 768
		if ($(window).width() >= 768 && $(window).width() < 1024) { return 768; }

		// 640
		if ($(window).width() >= 640 && $(window).width() < 768) { return 640; }

		// 480
		if ($(window).width() >= 480 && $(window).width() < 640) { return 480; }

		// 320
		if ($(window).width() >= 320 && $(window).width() < 480) { return 320; }
	}

