	function megaDropdownResponsive() {
		if("wcpss" == "wcpss") {
			if($(".hp").length) {
				var channelDropdownH = $("#gb-channel-height-marker").height() + 5; // ADD 5PX FOR BOTTOM BORDER ON GB-CHANNEL-LIST-OUTER
				$(".sw-channel-dropdown.outer").css("min-height", channelDropdownH + "px");

				// SET OUR VARIABLES
				var contW = $(".sw-channel-dropdown.outer").width();
		                var contH = channelDropdownH;
		                var contRatio = contH / contW;
		                var imgW = contW, imgH = 0;

		                // GET IMAGE RATIO
		                var initW = 1565, initH = 700;
		                var ratio = initH / initW;
		                var imgH = imgW * ratio;

		                if(imgH < contH){
		                    imgH = contH;
		                    imgW = imgH / ratio;
		                }

		                var centerW = (imgW - contW) / 2;
		                var centerH = (imgH - contH) / 2;

		                // APPLY THE DIMENSIONS
		                $(".mega-channel-bg-img img").css({"width": imgW + "px", "height": imgH + "px", "margin": "-" + centerH + "px 0px 0px -" + centerW + "px"});
			} else {
				// SET OUR VARIABLES
				var contW = $(".sw-channel-dropdown.outer").width();
		                var contH = $("#gb-channel-list ul.sw-channel-dropdown").outerHeight(true);
		                var contRatio = contH / contW;
		                var imgW = contW, imgH = 0;

		                // GET IMAGE RATIO
		                var initW = 1565, initH = 700;
		                var ratio = initH / initW;
		                var imgH = imgW * ratio;

		                if(imgH < contH){
		                    imgH = contH;
		                    imgW = imgH / ratio;
		                }

		                var centerW = (imgW - contW) / 2;
		                var centerH = (imgH - contH) / 2;

		                // APPLY THE DIMENSIONS
		                $(".mega-channel-bg-img img").css({"width": imgW + "px", "height": imgH + "px", "margin": "-" + centerH + "px 0px 0px -" + centerW + "px"});
			}
		}
	}

	function featuredPhotos() {
		var getWidth = jsBreakPoint();

		// REMOVE ALL APPS EXCEPT FOR FIRST HEADLINES APP IN HOMEPAGE REGION I
		$("#hp-content-bottom .ui-widget.app").not($("#hp-content-bottom .ui-widget.app.headlines").eq(0)).each(function() {
			$(this).remove();
		});

		// REMOVE HREFS FROM HEADLINE ARTICLES IN HOMEPAGE REGION I
		var headlinesClass = ["one", "two", "three", "four", "five", "six", "seven"];
		$("#hp-content-bottom .ui-widget.app.headlines .ui-articles li").each(function() {
			// UNWRAP SPAN
			$(".img img", this).unwrap();
			// SET A TAG
			var articleURL = jQuery.trim($(".ui-article-description", this).text().replace(/\r?\n|\r/g, ""));
			if(isValidURL(articleURL)) {
				$(".ui-article a", this).removeClass("ui-article-thumb").attr("href", articleURL).attr("target", "_blank");
			} else {
				$(".ui-article img", this).unwrap();
			}
			// UNWRAP TITLE
			$(".ui-article-title span", this).unwrap();
			var articleIndex = $(this).index();
			$(this).addClass("article-" + headlinesClass[articleIndex]);
		});

		// LIMIT RECORDS TO 7 IN HOMEPAGE REGION I
        	$("#hp-content-bottom .ui-widget.app.headlines .ui-articles li:gt(6)").remove();

        	// ADD CAPTION TO HEADLINES IN HOMEPAGE REGION I
        	var headlineCaption = "true";
		if(headlineCaption == "true") {
			$("#hp-content-bottom .headlines").addClass("headlines-caption");
		}

		// WRAP UI-ARTICLES WITH VIEWER DIV
		$("#hp-content-bottom .ui-widget.app.headlines .ui-articles").wrap("
");

		$("#hp-content-bottom div.ui-widget.app.headlines li").last().addClass("last");
		$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").css("margin-left","0");

		var viewerW = $("#hp-content-bottom div.ui-widget.app.headlines #hp-featured-photos-viewer").width();
		var headlineW = $("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").length * viewerW;

		if (getWidth == 960) { // 960
			$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles, #hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").removeAttr("style");
		} else {
			$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").css("width", headlineW);
			$("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").css("width", viewerW + "px");
		}

		// SCROLL FUNCTION
		$(".hp-featured-control").click(function(e) {
			e.preventDefault();
			var scrollDistance = $("#hp-content-bottom div.ui-widget.app.headlines #hp-featured-photos-viewer").width();
			var firstScrollDistance = $("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").first().outerWidth(true);
			var lastScrollDistance = $("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").last().outerWidth(true);
			var slidePos = parseInt($("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").css("margin-left")) * -1;
			var overflowDistance = parseInt($("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").css("width")) - $("#hp-content-bottom #hp-featured-photos-viewer").width();
			var lastScroll = overflowDistance - slidePos;
			var newPos = 0;

			// ANIMATE THE THUMBNAIL SLIDE
			if($(this).hasClass("next")) {
			    if(lastScroll < lastScrollDistance) {
			        newPos = slidePos + lastScroll;
			    } else {
			        newPos = slidePos + scrollDistance;
			    }    

			    $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").animate({
			        marginLeft: "-" + newPos + "px"
			    }, 800);
			} else if($(this).hasClass("back")) {
			    if(slidePos < firstScrollDistance) {
			        newPos = 0;
			    } else {
			        newPos = slidePos - scrollDistance;   
			    }

			    $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").animate({
			        marginLeft: "-" + newPos + "px"
			    }, 800);
			}	
		});

	}

	function featuredPhotosResize() {
		var getWidth = jsBreakPoint();

		$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").css("margin-left","0");

		var viewerW = $("#hp-content-bottom div.ui-widget.app.headlines #hp-featured-photos-viewer").width();
		var headlineW = $("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").length * viewerW;

		if (getWidth == 960) { // 960
			$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles, #hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").removeAttr("style");
		} else {
			$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").css("width", headlineW);
			$("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail .ui-articles li").css("width", viewerW + "px");
		}
	}

	// FOR URL VALIDATING
	function isValidURL(url){
		var RegExp = /(ftp|http|https):\/\/(\w+:{0,1}\w*@)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%@!\-\/]))?/;
		if(RegExp.test(url)){
			return true;
		} else {
			return false;
		}
	}

    function schoolListMenu() {
        var elementarySchools = [], middleSchools = [], highSchools = [], altSchools = [];
        var elementaryA_G = [], elementaryH_N = [], elementaryO_Z = [];
        var A_G = ["a", "b", "c", "d", "e", "f", "g"];
        var H_N = ["h", "i", "j", "k", "l", "m", "n"];
        var O_Z = ["o", "p", "q", "r", "s", "t", "u", "v", "w", "x", "y", "z"];


        // SEPARATE SCHOOLS INTO THEIR OWN ARRAYS
        for(var school in schoolList) {
            if(schoolList[school].schoolVisible.toLowerCase() == 'true') {
                var thisType = schoolList[school].schoolType.toLowerCase();

                // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                if(schoolList[school].schoolType.indexOf(",") > -1) {
                    var subTypes = schoolList[school].schoolType.split(",");

                    // LOOP THROUGH THE SUB TYPES
                    for(var subType in subTypes) {
                        var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                        switch(thisSubType) {
                            case "elementary":
                                elementarySchools.push(schoolList[school]);

                                var schoolNameFirstChar = schoolList[school].schoolName.charAt(0).toLowerCase();

                                // IF BEGINNING OF THE SCHOOL NAME STARTS WITH A LETTER BETWEEN A AND G
                                if(A_G.indexOf(schoolNameFirstChar) > -1) {
                                    elementaryA_G.push(schoolList[school]);
                                }

                                // IF BEGINNING OF THE SCHOOL NAME STARTS WITH A LETTER BETWEEN H AND N
                                else if(H_N.indexOf(schoolNameFirstChar) > -1) {
                                    elementaryH_N.push(schoolList[school]);
                                }

                                // IF BEGINNING OF THE SCHOOL NAME STARTS WITH A LETTER BETWEEN O AND Z
                                else if(O_Z.indexOf(schoolNameFirstChar) > -1) {
                                    elementaryO_Z.push(schoolList[school]);
                                }
                            break;
                            case "middle":
                                middleSchools.push(schoolList[school]);
                            break;
                            case "high":
                                highSchools.push(schoolList[school]);
                            break;
                            case "special":
                                altSchools.push(schoolList[school]);
                            break;
                        }
                    }
                }

                // SINGLE TYPE VALUE
                else {
                    switch(thisType) {
                        case "elementary":
                            elementarySchools.push(schoolList[school]);

                            var schoolNameFirstChar = schoolList[school].schoolName.charAt(0).toLowerCase();

                            // IF BEGINNING OF THE SCHOOL NAME STARTS WITH A LETTER BETWEEN A AND G
                            if(A_G.indexOf(schoolNameFirstChar) > -1) {
                                elementaryA_G.push(schoolList[school]);
                            }

                            // IF BEGINNING OF THE SCHOOL NAME STARTS WITH A LETTER BETWEEN H AND N
                            else if(H_N.indexOf(schoolNameFirstChar) > -1) {
                                elementaryH_N.push(schoolList[school]);
                            }

                            // IF BEGINNING OF THE SCHOOL NAME STARTS WITH A LETTER BETWEEN O AND Z
                            else if(O_Z.indexOf(schoolNameFirstChar) > -1) {
                                elementaryO_Z.push(schoolList[school]);
                            }
                        break;
                        case "middle":
                            middleSchools.push(schoolList[school]);
                        break;
                        case "high":
                            highSchools.push(schoolList[school]);
                        break;
                        case "special":
                            altSchools.push(schoolList[school]);
                        break;
                    }
                }
            }
        }

        // SORT SCHOOL ARRAYS ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES OR MINIBASE ISNT IN ORDER
        elementarySchools.sort(sortSchoolArray);
        middleSchools.sort(sortSchoolArray);
        highSchools.sort(sortSchoolArray);
        altSchools.sort(sortSchoolArray);
        elementaryA_G.sort(sortSchoolArray);
        elementaryH_N.sort(sortSchoolArray);
        elementaryO_Z.sort(sortSchoolArray);

        // ADD THE PANELS TO THE DOM
        $("#gb-elem-list-full").html(buildSchoolColumns(elementarySchools, "elem", 5, "gb-school-list-column "));
        $(".gb-school-list-section.middle").html(buildSchoolColumns(middleSchools, "middle", 2, "gb-school-list-column "));
        $(".gb-school-list-section.high").html(buildSchoolColumns(highSchools, "high", 2, "gb-school-list-column "));
        $(".gb-school-list-section.alt").html(buildSchoolColumns(altSchools, "alt", 1, "gb-school-list-column "));

        // ADD THE ELEMENTARY MOBILE ACCORDION TO THE DOM
        $(".gb-elem-cat.a-g").append(buildSchoolColumns(elementaryA_G, "", 1, "gb-elem-cat-list", "gb-elem-cat-list-1"));
        $(".gb-elem-cat.h-n").append(buildSchoolColumns(elementaryH_N, "", 1, "gb-elem-cat-list", "gb-elem-cat-list-2"));
        $(".gb-elem-cat.o-z").append(buildSchoolColumns(elementaryO_Z, "", 1, "gb-elem-cat-list", "gb-elem-cat-list-3"));

        // BIND THE MENU EVENTS
        schoolListEvents();
	}

    function sortSchoolArray(a, b) {
    	if(a.schoolName > b.schoolName) {
            return 1;
        }
        if(a.schoolName < b.schoolName) {
            return -1;
        }
        return 0;
    }

    function buildSchoolColumns(schoolArray, schoolType, columnNum, columnClass, columnID) {
    	// REFERENCE - http://codepen.io/uxbox/pen/fpLJl

        // DEFINE ARRAY THAT WILL HOLD NUMBER OF LINKS FOR EACH COLUMN
        var linksPerCol = [];

        // GET MINIMUM NUMBER OF SCHOOLS PER COLUMN
        var minLinkNum = Math.floor(schoolArray.length / columnNum);

        // GET THE NUMBER OF UNEVEN REMAINDERS
        var remainders = schoolArray.length - (minLinkNum * columnNum);

        // LOOP FOUR TIMES BECAUSE THERE ARE FOUR COLUMNS
        for (var i = 0; i < columnNum; i++) {
            // IF LOOP INDEX IS LESS THAN THE REMAINDER NUMBER
            if (i < remainders) {
                // ADD ONE TO THIS COLUMN LINK NUMBER SO THEN REMAINDER LINKS WILL BE DISTRIBUTED EVENLY
                linksPerCol[i] = minLinkNum + 1;
            } else {
                // THIS WILL BE THE MINIMUM
                linksPerCol[i] = minLinkNum;
            }
        }

        // NOW LOOP AGAIN FOUR TIMES TO BUILD THE COLUMNS - FOUR TIMES BECAUSE THERE ARE FOUR COLUMNS
        var schoolColumns = "";
        for (var i = 0; i < columnNum; i++) {
        	var columnAttrs = (columnClass == "gb-elem-cat-list") ? ' id="' + columnID + '" aria-hidden="true"' : '';
            schoolColumns += '
';

            // LOOP THROUGH THE LINKS PER COLUMN ARRAY
            for (var j = 0; j < linksPerCol[i]; j++) {
                // BUILD THE NEXT ITEM INDEX
                var recordIndex = 0;
                for (var k = 0; k < i; k++) {
                    recordIndex += linksPerCol[k];
                }

                // BUILD THE LINK ITEM
                schoolColumns += '
' + schoolArray[j + recordIndex].schoolName + '';
            }
            schoolColumns += '';
        }

        // RETURN THE ENTIRE STRUCTURE
        return schoolColumns;
    }

    function schoolListEvents(){
		// CLICK FUNCTION FOR SCHOOL DROP-DOWN
        $(document).on("click keydown", "#school-select-btn", function(e){
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
            	e.preventDefault();

                // MENU IS NOT OPEN
                if(!$("#gb-school-list-outer").hasClass("open")){
                	// MAKE SURE ALL ATTRIBUTES ARE RESET
                    $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");

                    // SET ELEMENTARY TAB AND PANEL AS ACTIVE
            		$(".gb-school-list-header:first").attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");
            		$(".gb-school-list-section:first").attr({"aria-hidden": "false"}).addClass("active");

                    // OPEN THE MENU
                    $("#gb-school-list-outer").slideDown(function(){
                    	// FOCUS THE FIRST TAB
                        $(".gb-school-list-header:first").focus();

                        // SET CLASSES
                        $("#school-select-btn").removeClass("closed").addClass("open").attr("aria-expanded", "true");
                        $(this).removeClass("closed").addClass("open");
                    }).attr("aria-hidden", "false");
                }

                // MENU IS OPEN
                else {
                	// CLOSE THE MENU
                    $("#gb-school-list-outer").slideUp(function(){
                    	// RESET ATTRIBUTES
                        $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    	$(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");

                        // SET CLASSES
                        $("#school-select-btn").removeClass("open").addClass("closed").attr("aria-expanded", "false");
                        $(this).removeClass("open").addClass("closed");
                    }).attr("aria-hidden", "true");
                }
            }
        });

		// TAB HEADER CLICK
		$(".gb-school-list-header").focus(function(e){
			e.preventDefault();

        	// ADJUST TAB ATTRIBUTES
            $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

            // ADJUST PANEL ATTRIBUTES
            $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
            $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).addClass("active");
		});

        // IE TAB CLICK
        $(".ie .gb-school-list-tab span:first").click(function() {
        	$(this).parent().focus();
        });

        // SCHOOL TAB KEYDOWN
        $(".gb-school-list-header").keydown(function(e) {
        	// IF SHIFT+TAB FOCUS THE SCHOOLS BUTTON
            if(e.shiftKey && e.keyCode == csKeyCodes.tab) {
            	e.preventDefault();
            	$("#school-select-btn").focus();
            } else {
        		handleTabListKey($(this), ".gb-school-list-header", e);
            }
        });

        // CLOSE MENU WITH ESC
        $(".gb-school-list-header, .gb-school-list-section").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.esc) {
            	e.preventDefault();

                // CLOSE THE MENU
                $("#gb-school-list-outer").slideUp(function(){
                    // RESET ATTRIBUTES
                    $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");

                    // SET CLASSES
                    $("#school-select-btn").removeClass("open").addClass("closed");
                    $(this).removeClass("open").addClass("closed");
                }).attr("aria-hidden", "true");

                $("#school-select-btn").attr("aria-expanded", "false").focus();
            }
        });

        // SCHOOL MENU LINK KEYDOWN
        $(".gb-school-list-section a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT ARROW
                case csKeyCodes.left:
                	e.preventDefault();

                    // FOCUS THE LEFT ITEM
                    if ($(this).parent().parent().parent().prev(".gb-school-list-column").find("ul > li").eq($(this).parent().index() + 1).length) { 
                        $(this).parent().parent().parent().prev(".gb-school-list-column").find("ul > li:nth-child(" + ($(this).parent().index() + 1) + ") a").focus();
                    } else { 
                        return false;
                    }
                break;

                // CONSUME UP ARROW
                case csKeyCodes.up:
                    e.preventDefault();

                    // FOCUS THE PREVIOUS ITEM
                    if ($(this).parent().is(":first-child")) { 
                        // FOCUS FIRST ITEM IN NEXT COLUMN
                    	if($(this).parent().parent().parent().prev(".gb-school-list-column").length) {
                        	$(this).parent().parent().parent().prev(".gb-school-list-column").find("ul > li:last-child a").focus();
                        } else {
                        	$(this).parent().parent().parent().parent().find(".gb-school-list-column:last ul > li:last-child a").focus();
                        }
                    } else { 
                        $(this).parent().prev("li").find("a").focus();
                    } 
                break;

                // CONSUME RIGHT ARROW
                case csKeyCodes.right:
                	e.preventDefault();

                    // FOCUS THE LEFT ITEM
                    if ($(this).parent().parent().parent().next(".gb-school-list-column").find("ul > li").eq($(this).parent().index() + 1).length) { 
                        $(this).parent().parent().parent().next(".gb-school-list-column").find("ul > li:nth-child(" + ($(this).parent().index() + 1) + ") a").focus();
                    } else { 
                        return false;
                    }
                break;

                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                    e.preventDefault();

                    // FOCUS THE NEXT ITEM
                    if ($(this).parent().is(":last-child")) {
                    	// FOCUS FIRST ITEM IN NEXT COLUMN
                    	if($(this).parent().parent().parent().next(".gb-school-list-column").length) {
                        	$(this).parent().parent().parent().next(".gb-school-list-column").find("ul > li:first-child a").focus();
                        } else {
                        	$(this).parent().parent().parent().parent().find(".gb-school-list-column:first ul > li:first-child a").focus();
                        }
                    } else { 
                        $(this).parent().next("li").find("a").focus();
                    } 
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS THE FIRST ITEM
                    $(this).parent().parent().find("li:first-child a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS THE LAST ITEM
                    $(this).parent().parent().find("li:last-child a").focus();
                break;
            }
        });

		// ELEMENTARY ACCORDION CLICK
		$(document).on("click keydown", ".gb-elem-cat-header", function(e) {
			if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();

                // IF THE SELECTED ACCORDION MENU OPTION ISNT ALREADY ACTIVE
                if(!$(this).parent().hasClass("active")){
                    // GET CLASS OF SELECTED ACCORDION MENU OPTION
                    var selectedCat = $(this).parent().attr("class");
                    selectedCat = jQuery.trim(selectedCat.replace("gb-elem-cat ", ""));

                    $(".gb-elem-cat.active .gb-elem-cat-list").slideUp().attr("aria-hidden", "true");
                    $(".gb-elem-cat." + selectedCat + " .gb-elem-cat-list").slideDown(function() {
                    	$("> ul > li:first-child a", this).focus();
                    }).attr("aria-hidden", "false");

                    // HIDE THE ACTIVE ACCORDION MENU OPTION
                    $(".gb-elem-cat.active").removeClass("active");
                    $(".gb-elem-cat-header").attr("aria-pressed", "false");
                    // SHOW THE CHOSEN ONE
                    $(".gb-elem-cat." + selectedCat).addClass("active");
                    $(this).attr("aria-pressed", "true");
                }
            }
		});
	}

    function handleTabListKey(tab, tabClass, event) {
    	var lastIndex = $(tabClass).length - 1;

        switch(event.keyCode){
        	// CONSUME LEFT AND UP ARROWS
            case csKeyCodes.left:
            case csKeyCodes.up:
            	event.preventDefault();

                // FOCUS THE PREVIOUS TAB
                if (tab.index() == 0) { 
                    $(tabClass + ":last-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() - 1).focus();
                } 
            break;

            // CONSUME RIGHT AND DOWN ARROWS
            case csKeyCodes.right:
            case csKeyCodes.down:
            	event.preventDefault();

                // FOCUS THE NEXT TAB
                if (tab.index() == lastIndex) { 
                    $(tabClass + ":first-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() + 1).focus();
                }
            break;

            // CONSUME HOME KEY
            case csKeyCodes.home:
            	event.preventDefault();

                // FOCUS THE FIRST TAB
                $(tabClass + ":first-child").focus();
            break;

            // CONSUME END KEY
            case csKeyCodes.end:
            	event.preventDefault();

                // FOCUS THE LAST TAB
                $(tabClass + ":last-child").focus();
            break;
        }
    }

