// CHECK IF ALERT NEEDS TO BE SHOWN
var schools = jQuery.trim($("#module-content-11500 .ui-widget.app.cs-alert-notification .cs-alert-check").text());
if(schools != "") {
	// BREAK THE SCHOOLS APART
	schools = schools.split(";");

	for(var s in schools) {
		// CHECK TO SEE IF THE SITE IS CHECKED
		var check = schools[s].split(":");
		if($.trim("wcpss").toLowerCase() == check[0]) {
			if(check[1] == "true") {
				$("#module-content-11500 .ui-widget.app.cs-alert-notification").removeClass("hidden");
			}
		}
	}
}

