    function LoadRating3407164937() {
    var ModuleInstanceID = 3407;
    var FlexDataID = 164937;

        GetContent("https://www.wcpss.net//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating3407164937", 2);
}

    function InsertRating3407164937() {
    var ModuleInstanceID = 3407;
    var FlexDataID = 164937;
    var Rating = $('#swmoduleinstancerating-hid-rating3407164937').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating3407164937();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.wcpss.net//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating3407164937']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating3407164937Values();
                InsertRating3407164937($('#swmoduleinstancerating-hid-rating3407164937').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating3407164937Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating3407164937']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating3407164937').val((i + 1) / 2);
            }
        });
    }

