$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=2G9gEwsOBd8%2bY0Z3ZejLYg%3d%3d&SessionID=a7ac75cc-b94d-4acb-b163-28e1d7b4d399&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+9%3a51%3a45+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.4.11';
